INSERT INTO jos_categories (`id`,`parent_id`,`title`,`name`,`image`,`section`,`image_position`,`description`,`published`,`checked_out`,`checked_out_time`,`editor`,`ordering`,`access`,`count`,`params`,`alias`) VALUES ( '7','0','Poesie','Poesie','key.jpg','1','left','Esse sono un esperimento stilistico e formale: nascono fondamentalmente          dall\'idea che - a mio parere - ha tormentato tutti noi: ma i critici di          letteratura <em>come cacchio fanno </em>ad essere cos&igrave; sicuri che l\'autore di cui si occupano alludesse proprio a ci&ograve; che pensano loro (mi riferisco in particolare a Dante e gli autori latini)?!? Quest\'opera nasce come parodia ma anche come esperimento compositivo. Sono state realizzate tutte a met&agrave; liceo (intorno ai 16 anni) tranne l\'ultima che risale al secondo anno di Universit&agrave; (anno in cui il Carlesso ha condiviso appartamento con Enrico Biondini).<br /> ','1','0','0000-00-00 00:00:00','','6','0','2','','poesie');
