INSERT INTO jos_categories (`id`,`parent_id`,`title`,`name`,`image`,`section`,`image_position`,`description`,`published`,`checked_out`,`checked_out_time`,`editor`,`ordering`,`access`,`count`,`params`,`alias`) VALUES ( '13','0','Seghe Mentali','Seghe Mentali','articles.jpg','1','left','Queste opere ad alto contenuto matematico/scientifico/erotico sono elucubrazioni          da ingegnere sul mondo femminile e su altri campi dello scibile umano.          Potrete liberamente scaricarle qui. Ci&ograve; che le accomuna &egrave;          la sega mentale. Per sapere di pi&ugrave; di questo esclusivissimo club,          vi invito a visitare il sito del <a href=\"http://www.seghementali.org/\">C.S.M</a>.','1','0','0000-00-00 00:00:00','','7','0','0','','seghe-mentali');
