INSERT INTO jos_categories (`id`,`parent_id`,`title`,`name`,`image`,`section`,`image_position`,`description`,`published`,`checked_out`,`checked_out_time`,`editor`,`ordering`,`access`,`count`,`params`,`alias`) VALUES ( '23','0','Linux','Linux','','3','right','<p>Tutto ci&ograve; che ha a che fare con Linux, l&#39;open source, .<br />Leggete qui cosa dice il suo creatore a una domanda sulla scelta tra il popolare pinguino e il robusto diavoletto...</p><p>&nbsp;</p><address>&gt; &gt; Other than the fact Linux has a cool name, could someone explain why I</address><address>&gt; &gt; should use Linux over BSD?</address><address>&gt;</address><address>&gt; No.&nbsp; That&#39;s it.&nbsp; The cool name, that is.&nbsp; We worked very hard on</address><address>&gt; creating a name that would appeal to the majority of people, and it</address><address>&gt; certainly paid off: thousands of people are using linux just to be able</address><address>&gt; to say &quot;OS/2? Hah.&nbsp; I&#39;ve got Linux.&nbsp; What a cool name&quot;.&nbsp; 386BSD made the</address><address>&gt; mistake of putting a lot of numbers and weird abbreviations into the</address><address>&gt; name, and is scaring away a lot of people just because it sounds too</address><address>&gt; technical.</address><address>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -- Linus Torvalds&#39; follow-up to a question about Linux</address><address>&nbsp;</address>','1','0','0000-00-00 00:00:00','','2','0','0','imagefolders=*2*','linux');
INSERT INTO jos_categories (`id`,`parent_id`,`title`,`name`,`image`,`section`,`image_position`,`description`,`published`,`checked_out`,`checked_out_time`,`editor`,`ordering`,`access`,`count`,`params`,`alias`) VALUES ( '28','0','Il glossario dialettale del Carlesso','Il glossario dialettale del Carlesso','carlesso.jpg','com_glossary','right','<p>Il dizionario dialettale del <strong>Carlesso</strong>. <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-cool.gif\" border=\"0\" alt=\"Cool\" title=\"Cool\" />  </p><hr width=\"100%\" size=\"2\" />Ho cercato di raccogliere qui quante piu&#39; parole tipiche delle citt&agrave; che mi circondano: tenter&ograve; dunque di esplorare modi di dire delle varie Ferrara, Bologna, Forl&igrave;, Rimini... Siete quanto mai pregati di spedirmi (se emilio-romagnoli) parole che non ho incluso! <br />Un particolare ringraziamento ai collaboratori che mi hanno fornito termini nuovi e correzioni: Stefano Giovannini, Fraffo, Karaoke, Andrea Fabrizi, Riccardo B., Bruno D.,Leonardo A., Dr. Nick, Francesco C., Francesca A., ...<br /><strong><!--  var prefix = \'&#109;a\' + \'i&#108;\' + \'&#116;o\';  var path = \'hr\' + \'ef\' + \'=\';  var addy97370 = \'p&#97;ll&#97;d&#105;&#117;s\' + \'&#64;\';  addy97370 = addy97370 + \'p&#97;ll&#97;d&#105;&#117;s\' + \'&#46;\' + \'&#105;t\';  var addy_text97370 = \'scrivetemi\';  document.write( \'<a \' + path + \'\\\'\' + prefix + \':\' + addy97370 + \'\\\'>\' );  document.write( addy_text97370 );  document.write( \'<\\/a>\' );  //-->Se volete contribuire anche voi all&#39;opera... <a href=\"mailto:palladius@palladius.it\">scrivetemi</a><!--  document.write( \'<span style=\\\'display: none;\\\'>\' );  //--><span style=\"display: none\">Indirizzo e-mail protetto dal bots spam , deve abilitare Javascript per vederlo    <!--  document.write( \'</\' );  document.write( \'span>\' );  //-->  </span>    !! </strong><em>(possibilmente mandandomi la parola con provincia, significato ed esempi cos&igrave; da facilitarmi il copia/incolla)</em><p>&nbsp;</p>','1','0','0000-00-00 00:00:00','','1','0','0','','il-glossario-dialettale-del-carlesso');
