INSERT INTO jos_contact_details (`id`,`name`,`con_position`,`address`,`suburb`,`state`,`country`,`postcode`,`telephone`,`fax`,`misc`,`image`,`imagepos`,`email_to`,`default_con`,`published`,`checked_out`,`checked_out_time`,`ordering`,`params`,`user_id`,`catid`,`access`,`alias`) VALUES ( '2','Silvia Cadonici','','','','','','','','','una mia cara amica di Parma, molto amante degli indovinelli','','','scrivi tu silvia','0','1','0','0000-00-00 00:00:00','1','show_image=-1\npageclass_sfx=\nprint=\nback_button=\nshow_name=1\nshow_position=1\nshow_icon_email=0\nshow_street_icon_address=1\nshow_suburb=1\nshow_state=1\nshow_country=1\nshow_postcode=1\nshow_icon_telephone=1\nshow_icon_fax=1\nshow_misc=1\nimage=1\nallow_vcard=1\nemail_description=1\nemail_description=\nshow_email_form=1\nshow_email_copy=1\ndrop_down=0\ncontact_contact_icons=0\nicon_icon_address=\nicon_show_icon_email=\nicon_show_icon_telephone=\nicon_show_icon_fax=\nicon_show_misc=','63','16','0','silvia-cadonici');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '1','Capire gl\'ingegneri','Capire gl\'ingegneri','<p><strong>Esperienza n. 1</strong><br />  Due studenti in Ingegneria camminano verso la loro residenza, quando uno dice all&#39;altro, con fare ammirato: &quot;Dove hai trovato questa bicicletta?&quot; Il secondo risponde:<br />  - Beh, dunque, ieri stavo camminando ed ero assorto nei miei pensieri, ad un certo punto incrocio una stragnocca in bicicletta che si ferma davanti a me, posa la bicicletta in terra, si spoglia completamente nuda e mi dice: &quot;Prendi ci&ograve; che vuoi!&quot;<br />Il primo annuisce e gli dice: &quot;Hai ragione, i vestiti sarebbero stati certamente troppo stretti!!!&quot;  	 <br /> </p>','<strong>Esperienza n. 2</strong><br /> Per una persona ottimista, il bicchiere e&#39; mezzo pieno. Per una persona pessimista, il bicchiere e&#39; mezzo vuoto. Per l&#39;ingegnere il bicchiere e&#39; due volte pi&ugrave; grande del necessario. <br /> <br /> <strong>Esperienza n. 3</strong><br />  Un prete, un medico e un ingegnere giocano a golf. Devono fare la coda appresso a un gruppo di giocatori particolarmente lenti. All&#39;improvviso, l&#39;ingegnere esplode e dice: - Ma che diavolo fanno? E&#39; un quarto d&#39;ora che aspettiamo! Il dottore interviene, anch&#39;egli esasperato: - Non lo so, ma non avevo mai visto persone tanto in difficolt&agrave;! Al che il prete dice: - Aspettate, ecco qualcuno del golf. Non dobbiamo fare altro che chiedere a lui... Scusatemi! Dite, per favore, c&#39;e&#39; qualche problema con il gruppo l&agrave; davanti? Sono piuttosto lenti, no? L&#39;altro risponde: - Ah si, e&#39; un gruppo di pompieri ciechi. Hanno tutti perso la vista cercando di salvare il golf dalle fiamme l&#39;anno scorso, e da allora li si lascia giocare gratis. Il gruppo resta in silenzio un momento, poi il prete dice: - E&#39; molto triste. Questa sera pregher&ograve; per loro ... Il medico aggiunge: - Ottima idea. Quanto a me, contatter&ograve; un collega oftalmologo per verificare che cosa pu&ograve; fare... E l&#39;ingegnere: - Ma perch&eacute; non giocano di notte???   	 <br /> <br /> <strong>Esperienza n. 4</strong><br /> Tre studenti in Ingegneria discutono del o degli eventuali creatori del corpo umano. Il primo dice: - Per me, era un ingegnere meccanico: guarda tutte queste articolazioni! Il secondo risponde: - Ma no! Era un ingegnere elettronico! Guarda, il sistema nervoso ha migliaia e migliaia di connessioni elettriche. Il terzo ribatte: - No, in realt&agrave; era un ingegnere civile: chi altri avrebbe potuto pensare di fare passare un canale per rifiuti tossici in mezzo a un parco divertimenti?  	 <br /> <br /> <strong>Esperienza n. 5</strong><br />  Un giovane sta camminando vicino ad una palude, quando ad un tratto una rana gli salta sulla spalla e gli dice: &quot;Ascolta, sono una bellissima principessa, dammi un bacio, torner&ograve; quella che ero e ti amer&ograve; per sempre!&quot; Il tipo la prende, e se la mette in tasca. Questa riprende: &quot;Sono una bellissima principessa, se mi baci riprender&ograve; la forma che avevo e sar&ograve; per sempre tua!!!&quot; Non capita nulla. Allora la rana inizia a strepitare: &quot;Mi hai capito? Sono una principessa bellissima: baciami e avrai per sempre una donna stupenda al tuo fianco!!!&quot; xAl che, il tipo risponde: &quot;Ascolta: sono un ingegnere, non ho tempo per le donne, ma una rana che parla e&#39; una figata!!!&quot;','0','3','0','1','2004-06-12 11:54:06','62','Web Master','2007-12-24 15:00:09','64','0','0000-00-00 00:00:00','2004-01-01 00:00:00','0000-00-00 00:00:00','asterisk.png|left|Joomla! Logo|1|Example Caption|bottom|center|120','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','4','0','9','','','0','215','capire-glingegneri');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '2','Indovinello dei cinque pirati','','<p><em>Questo mi fu raccontato dalla mia amica Elisa e devo ammettere essere il mio preferito (forse perch&eacute; &egrave; discretamente complicato, l&#39;ho risolto in pochi minuti, e ci ho fatto un figurone con costei). Purtroppo una volta capito il trucco i calcoli vengono abbastanza semplici. Credo si riconduca (ma sono ignorante a rguardo) alla teoria dei giochi di Nash.</em><br /></p>','Cinque pirati devono spartirsi un tesoro di 1000 monte d&#39;oro. Tra di loro c&#39;&egrave; una gerarchia, quindi diremo che l&#39;1 e&#39; il capo e cosi&#39; via fino al 5 che e&#39; l&#39;ultimo sfigato. Il capo ha la seguente idea che impone per dividersi il denaro: l&#39;ultimo fa una proposta di suddivisione (con essa intendendo una spartizione del denaro tale che la somma ammonti a 1000, da tutti a me a 200 a testa, fino a spartizioni piu&#39; fantasiose). Qualunque essa sia, tutti i presenti votano. Se si ha la maggioranza assoluta dei voti a favore (nel caso, 3 voti a favore), il gioco finisce e si fa esegue la proposta. Se no, il quinto viene ucciso e toccher&agrave; al quarto fare una suddivisione tra i rimanenti 4, e cosi&#39; via finche&#39; eventualmente non rimane il capo da solo. Sapendo che i 5 pirati sono logici eccellenti e che vogliono massimizzare il proprio profitto (e ancor piu&#39; vogliono rimanere vivi), dire che proposta fara&#39; il quinto. Clausola: i pirati voteranno in modo da massimizzare il loro profitto; se una soluzione mi da&#39; x e votandoti contro so che dopo avro&#39; anche solo una moneta in piu&#39;, ti votero&#39; contro; se tu mi dai anche solo una moneta in piu&#39;, ti votero&#39; a favore; in caso di parita&#39; i pirati son sanguinari e votano la morte del compagno.<br />  La soluzione c&#39;e&#39;, in particolare ve ne sono due equivalenti.  <img src=\"mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-wink.gif\" border=\"0\" alt=\"Wink\" title=\"Wink\" />   ','1','3','0','14','2004-08-09 08:30:34','62','','2007-11-04 21:02:02','64','0','0000-00-00 00:00:00','2004-08-09 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','6','0','6','','','0','2508','indovinello-dei-cinque-pirati');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '3','Modifiche al sito www.goliardia.it','Modifiche al sito www.goliardia.it',' <p>Se andate a vedere il sito <img border=\"0\" alt=\"Cool\" src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-cool.gif\" /> <a href=\"http://www.goliardia.it\">www.goliardia.it</a> , scoprirete interessanti novit&agrave;!</p> <p>Sto pensando di scorporare gli stili nel CSS, e di creare una sorta di almanacco (link a cose remote ma interessanti)</p> <p>Il sito alternativo lo trovate in&nbsp; <a href=\"http://www.goliardia.it/sito06/\">www.goliardia.it/sito06/</a></p><p>&nbsp;<img border=\"0\" src=\"file:///C:/DOCUME%7E1/riccardo/IMPOST%7E1/Temp/moz-screenshot-4.jpg\" /></p><p><img border=\"0\" src=\"file:///C:/DOCUME%7E1/riccardo/IMPOST%7E1/Temp/moz-screenshot-2.jpg\" /><img border=\"0\" src=\"file:///C:/DOCUME%7E1/riccardo/IMPOST%7E1/Temp/moz-screenshot-3.jpg\" /> </p>','<img border=\"0\" src=\"file:///C:/DOCUME%7E1/riccardo/IMPOST%7E1/Temp/moz-screenshot-5.jpg\" />','-1','2','0','3','2004-08-09 08:30:34','62','','2006-09-14 11:57:41','64','0','0000-00-00 00:00:00','2004-08-09 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','5','0','4','','','0','505','modifiche-al-sito-wwwgoliardiait');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '4','ICAP: un sito di indovinelli da perderci la testa','','<p>Se andate a guardare <a href=\"http://www.icap.altervista.org/\">http://www.icap.altervista.org/</a> (Il cervello a prostituirsi) vi troverete coinvolti in un interessante serie di indovinelli, la soluzione di ciascuno &egrave; il link al problema successivo! Io per ne ho risolti 7-8 e se volete vi incollo il codice con cui tramite linux mi risparmio un po&#39; di tempo... </p>','Copiate e incollate il codice che segue:&nbsp;        <pre>#!/bin/bash<br /># 	Fatto da Riccardo Carlesso,<br /><br /> <br />STRINGA=&quot;http://www.icap.altervista.org/parole/indovinate/finora/&quot;<br />  echo &quot;Stringa finora: $STRINGA&quot;<br />   function tentaparola {<br />   &nbsp; i=&quot;$1&quot;<br />  &nbsp; echo -en &quot;Provo $i/ : &quot;<br />  &nbsp;&nbsp; lynx -source $STRINGA$i/&nbsp; | grep -q &quot;vista molto spesso&quot; &amp;&amp; echo &quot;NO: $i&quot; || echo &quot;OK &#39;$i&#39;: modificalo mo dentro a $0!!!&quot;<br />   }<br />   <br />   echo &quot;Pagina:&quot;; lynx -source $STRINGA/<br />   <br />  echo &quot;Dimmi pure:&quot;<br />   while read i<br />   do<br />  &nbsp;tentaparola $i &amp;<br />  &nbsp; # qua potete provare ciappini vari sulla parola, tipo:<br />  &nbsp; #&nbsp; tentaparola $(<font color=\"red\">funzionesborona</font> $i) &amp;<br />  done<br />   <br />  </pre>  ','1','2','0','3','2004-08-09 08:30:34','62','','2006-09-17 17:03:43','64','0','0000-00-00 00:00:00','2004-08-09 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','5','0','6','icap indovinelli linux','Un sito di indovinelli molto interessante e uno script bash di linux fatto da carlesso per aiutare a risolverlo','0','597','icap-un-sito-di-indovinelli-da-perderci-la-testa');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '5','home','Riccardo Carlesso Home','<p><a href=\"javascript:void(0)\" onclick=\"window.open(\'http://www.palladius.it/joomlait//components/com_zoom/www/view.php?popup=1&q={obfs:225227208219224263274269286217223203263274271286219209259224215219214263275286220219208263275}\', \'win1\', \'width=550, height=550, scrollbars=1, resizable=1\').focus()\"><img src=\"http://www.palladius.it/joomlait/images/zoom/UNBRLV/thumbs/dscn7236.jpg\" border=\"1\" alt=\" \" title=\"Ric in giallo\" width=\"130\" height=\"168\" align=\"right\" /></a></p><p><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=blogcategory&amp;id=30&amp;Itemid=67\"><img src=\"http://www.palladius.it/joomlait/images/stories/english_flag.jpg\" alt=\"english version\" title=\"english version\" hspace=\"6\" width=\"36\" height=\"23\" align=\"left\" /></a> </p><em>Sorry! An <strong>English version</strong> of this site isn&#39;t available at the moment; some materia</em><em>l is being built right now. You can find it <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=blogcategory&amp;id=30&amp;Itemid=67\" title=\"English contents\">here</a>. </em><br /><p><strong>PS1</strong>. Add&igrave; 4 Sett &#39;08 peso<font color=\"#007000\"><strong> 85,5</strong> </font>kg (post corsetta per San Luca)<br /><strong>PS2</strong>. Oggi pigreco vale circa <strong><span class=\"zonagrassi\">3.1<em>399 </em></span></strong>(a causa della crisi dei subprime e dell&#39;Islanda)<br /><strong>Wii</strong>. L&#39;11 luglio 07 ho fatto il mio nuovo record a nuoto: <strong><span class=\"zonagrassi\">5,3km </span></strong>(<em>212 vasche</em>)</p> <p><strong>Benvenuti </strong>nel mio sito. Sono <strong>Riccardo Carlesso</strong> (anche noto come <strong>Palladius </strong>in <a href=\"http://www.goliardia.it/\">goliardia</a>, <strong>486 </strong>all&#39;Universit&agrave; e <strong>Rolando </strong>nel mondo internet degli anni &#39;90). In questo sito troverete, tra le altre cose:&nbsp; </p><ul><li><strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom\">Foto</a></strong> (trovate le vecchie foto nel <a href=\"http://www.palladius.it/carlesso/foto2.php\" target=\"_blank\">sito vecchio</a>). Trovate ad esempio foto dei miei viaggi a <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=4\">Santo Domingo</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=32\">Giappone</a>, <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=36\">Cile</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=41\">Lille</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=47\">Tunisia</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=49\">Cambridge</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=52\">Istanbul</a> ... oppure le <a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=42\">Feriae Matricolarum</a> pi&ugrave; gialle della storia. <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-wink.gif\" border=\"0\" alt=\"Wink\" title=\"Wink\" width=\"12\" height=\"12\" /></li><li>Le <strong>mie <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=category&amp;sectionid=1&amp;id=7&amp;Itemid=26\">poesie</a></strong>. </li><li>Un <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=63&amp;Itemid=26\">glossario </a></strong>di termini usati nel bolognese e nel resto dell&#39;emilia <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-cool.gif\" border=\"0\" alt=\"Cool\" title=\"Cool\" width=\"14\" height=\"14\" />(mi ha fruttato pure una fugace e gaffosa apparizione su una TV&nbsp; bolognese!). </li><li>Alcune mie <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=category&amp;sectionid=1&amp;id=13&amp;Itemid=29\">seghe mentali</a></strong> (a sfondo tipicamente sessuale e matematico)</li><li><strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=blogcategory&amp;id=14&amp;Itemid=33\">Indovinelli</a></strong>&nbsp; logici e matematici.<strong> <br /></strong></li><li><strong>Dicono di lui (<a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=72&amp;Itemid=26\" title=\"Ipse dixit\">Ipse dixit</a>) </strong>e <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=79&amp;Itemid=26\" title=\"giochi linguistici\">giuochi <strong>linguistici</strong></a> (<a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=152&amp;Itemid=29\">italiani </a>e in <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=79&amp;Itemid=26\">multilingue</a> ) .<br /></li><li><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=blogcategory&amp;id=29&amp;Itemid=66\"><strong>Diari di viaggio</strong></a> (di cui vado molto fiero): <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=170&amp;Itemid=26\" title=\"Diario di Monaco 08\">Monaco 08</a></strong> , Spagna 08, Dublino 08 (luglio), <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=164&amp;Itemid=26\" title=\"Diario di Dublino\"><strong>Dublino</strong></a> (apr 08), <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=161&amp;Itemid=26\">Friedrichshafen (feb 08)</a> , <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=155&amp;Itemid=26\">Istanbul</a></strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=155&amp;Itemid=26\"> (gen 08)</a>, <br />&nbsp;<strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=123&amp;Itemid=26\">Cile</a> </strong>(apr 07), <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=138&amp;Itemid=26\">Parigi</a></strong> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=129&amp;Itemid=26\"><strong>Friedrichshafen </strong>(giu 07)</a> , <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=134&amp;Itemid=26\">Tunisia</a></strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=134&amp;Itemid=26\"> (ago 07)</a><strong> </strong>, <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=138&amp;Itemid=26\">Lille</a></strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=138&amp;Itemid=26\"> (set 07)</a> , <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=140&amp;Itemid=26\">Cambridge/Londra</a></strong> (07) , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=147&amp;Itemid=26\" title=\"The night I decided to quit smoking\">Friedrichshafen (dic 07)</a>..., <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=126&amp;Itemid=26\">Giappone</a></strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=126&amp;Itemid=26\"> (nov 06)</a> ,.<br /></li><li><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=12&amp;Itemid=34\"><img src=\"http://www.palladius.it/joomlait/images/riccardo/chi%20sono.jpg\" alt=\"Chi sono\" title=\"Chi sono\" width=\"84\" height=\"61\" align=\"right\" /></a> Se volete conoscermi, cliccate su <strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=12&amp;Itemid=34\">chi sono</a></strong>, o leggete tutto ci&ograve; che pertiene il <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=category&amp;sectionid=1&amp;id=27&amp;Itemid=26\">Carlesso</a> (un&#39;intera sezione di materiale scritto da me su di me, insomma niente copia e incolla da internet, tutta roba originale, anche se d&#39;interesse discutibile <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-laughing.gif\" border=\"0\" alt=\"Laughing\" title=\"Laughing\" width=\"13\" height=\"13\" />), ad esempio: le <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=69&amp;Itemid=26\">cose che amo e odio</a> , le <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=86&amp;Itemid=26\">mie manie</a> , la mia <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=83&amp;Itemid=26\">dieta</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=82&amp;Itemid=26\">che libri leggo</a> , che <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=70&amp;Itemid=26\">musica ascolto</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=68&amp;Itemid=26\">cosa mangio</a> , <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=89&amp;Itemid=26\">che software uso</a> , ...</li><li>Siti esterni: <strong>foto </strong>su <strong><a href=\"http://www.flickr.com/photos/palladiusbonton/\">Flickr</a></strong>, cagate sul mio <strong><a href=\"http://www.facebook.com/profile.php?id=608193539\">Facebook</a></strong> , i miei <strong>video </strong>su <a href=\"http://it.youtube.com/profile?user=palladiusbonton\"><strong>Youtube</strong></a>,&nbsp; il mio <a href=\"http://www.palladius.it/carlesso/\">vecchio sito</a> .<br /></li></ul><div align=\"center\"><a href=\"http://www.goliardia.it/\" title=\"Il sito della gogliardia itagliana ;)\"><div style=\"text-align: center\"><img src=\"http://www.palladius.it/joomlait/images/banners/logoiside-10k.jpg\" border=\"1\" alt=\"Sito goliardia\" hspace=\"6\" width=\"204\" height=\"79\" align=\"right\" /></div></a></div><p><a href=\"http://www.goliardia.it/\"><strong>www.goliardia.it</strong></a> Ah! Sono particolarmente <em>orgoglione</em> della costruzione di un sito che &egrave; una vera e propria <em>community</em>, che vede collegate centinaia di persone ogni giorno; essa riunisce <strong>goliardi </strong>da tutta Italia. Potete accedervi cliccando sull&#39;icona a fianco o cliccando <a href=\"http://www.goliardia.it\">qui</a>. </p><p><strong>Sono particolarmente contrario alla privacy (come concetto). </strong>Alla mia per cominciare, tanto che ho pubblicato su internet un sacco di cose su di me, e addirittura il mio calendario. Nascondo solo gli eventi che possano ledere la privacy altrui o altrei (penso che ciascuno possa pensarla come vuole, addirittura - purch&eacute; non accada troppo spesso - anche diversamente da me). Pu&ograve; essere brutto che Google sappia tanto di me, ma a me d&agrave; tanto, e io gli do tanto. Se tanto mi d&agrave; tanto, oddio mi sta venendo il mal di testa. scusate. Ci&ograve; che voglio dire &egrave; che se un mio amico che sta a Londra, o Milano, vuole sapere che terre batto pu&ograve; collegarsi al mio Calendario, e ci&ograve; &egrave; bello. Se uno vuole rapirmi, gli rendo un servizio utile, e spero che almeno i legacci me li metta un po&#39; meno stretti, dopo. Credo che sarebbe pi&ugrave; bello un mondo in cui tutti si facessero <em>un po&#39; meno </em>i cazzi propri, pur restando accettabili certi segreti (ad esempio, con chi andiamo a letto, quanto guadagniamo, e se votiamo Forza Italia). Se volete divertirvi, <strong>iscrivetevi al mio calendario</strong> e scoprite cosa faccio nei prossimi giorni: il&nbsp; mio <a href=\"http://www.google.com/calendar/render?cid=isme2q59snntl039c69pb67r4g%40group.calendar.google.com\" target=\"_blank\"><img src=\"http://www.google.com/calendar/images/ext/gc_button1_it.gif\" border=\"0\" alt=\" \" width=\"100\" height=\"25\" align=\"middle\" /></a>&nbsp; ! Qui c&#39;&egrave; la versione <strong><a href=\"http://www.google.com/calendar/embed?src=dcs96o2p06ko2jci291vofbf5k%40group.calendar.google.com\" target=\"_blank\">HTML</a></strong> che pu&ograve; vedere anche un bambino, e addirittura un non vedente (bazze dell&#39;Accessibilit&agrave;...) </p><p><em>Vi consiglio la versione HTML per chi non ha un account a gmail</em>:<a href=\"http://www.google.com/calendar/ical/dcs96o2p06ko2jci291vofbf5k%40group.calendar.google.com/public/basic.ics\" target=\"_blank\">ICS</a> <a href=\"http://www.google.com/calendar/feeds/dcs96o2p06ko2jci291vofbf5k%40group.calendar.google.com/public/basic\" target=\"_blank\">XML</a> <strong><a href=\"http://www.google.com/calendar/embed?src=dcs96o2p06ko2jci291vofbf5k%40group.calendar.google.com\" target=\"_blank\">HTML</a></strong></p><h3 align=\"center\">Un po&#39; di foto<a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=13\"> </a></h3><table border=\"0\" align=\"center\"><tbody><tr><td class=\"top\" align=\"center\"><strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=4\"><img src=\"http://www.palladius.it/joomlait/images/zoom/WSILNF/thumbs/dsc00146.jpg\" alt=\"Ric a Santo Domingo\" title=\"Ric a Santo Domingo\" width=\"120\" height=\"160\" /></a></strong> </td><td class=\"top\" align=\"center\"><strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=12\"><br /></a></strong></td><td align=\"center\"><strong>&nbsp;</strong></td><td align=\"center\"><h3><strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=13\"><img src=\"http://www.palladius.it/joomlait/images/zoom/Sardegna%202006/thumbs/immagine_003.jpg\" border=\"0\" alt=\"Ric in Sardegna\" width=\"120\" height=\"160\" /></a></strong></h3></td><td align=\"center\"><strong><br /></strong></td><td align=\"center\"><h3><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=21\"><img src=\"http://www.palladius.it/joomlait/images/zoom/WMVKGE/thumbs/dscn2418.jpg\" border=\"0\" alt=\"Sardegna Agosto\" width=\"120\" height=\"160\" /></a> </h3></td><td><div style=\"text-align: center\"><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=54&amp;catid=32\"><img src=\"http://www.palladius.it/joomlait/images/riccardo/ave%20ric%20e%20dave%20in%20kimono%20320x240.jpg\" alt=\"Ave Ric e Dave in kimono\" width=\"210\" height=\"157\" /></a></div></td><td><p align=\"center\"><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=40\"><img src=\"http://www.palladius.it/joomlait/images/riccardo/riccilethumb.jpg\" alt=\"Riccardo in Cile\" title=\"Riccardo in Cile\" width=\"186\" height=\"139\" /></a></p></td></tr><tr><td align=\"center\"><p><strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=4\">Santo Domingo</a><br />Agosto 05 <br /></strong></p></td><td align=\"center\"><p><strong><br /></strong></p></td><td align=\"center\"><strong>&nbsp;</strong></td><td align=\"center\"><p><strong>&nbsp;</strong><strong>Sardegna<a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=13\"><br />Giugno &#39;06</a></strong></p></td><td align=\"center\">&nbsp;</td><td align=\"center\">&nbsp;&nbsp;<strong>Sardegna<a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=21\"><br />Agosto &#39;06</a> </strong></td><td align=\"center\"><p>&nbsp;<strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=32\">Giappone</a> ,<br />Novembre &#39;06&nbsp;</strong></p></td><td align=\"center\"><p><strong><a href=\"http://www.palladius.it/joomlait/index.php?option=com_zoom&amp;Itemid=35&amp;catid=40\">Cile</a> ,<br />Marzo &#39;07</strong> </p></td></tr></tbody></table><p><br /><strong>NB</strong> Questo sito &egrave; costruito con <a href=\"http://www.joomla.it/\">Joomla!</a>, che &egrave; un CMS (Content Management System) molto bello. E&#39; <a href=\"http://www.opensource.org/\">open source</a>, che per gl&#39;ignoranti pu&ograve; suonare come &#39;agratis&#39;. In realt&agrave; &egrave; molto di pi&ugrave; che gratis. E&#39; totale libert&agrave;, &egrave; comunione dei beni al di fuori del matrimonio, ... In pratica questo CMS mi consente di inserire contenuti con un click senza dover fare barboso editing e push di pagine HTML a ogni pi&eacute; sospinto...</p><table border=\"0\"><tbody><tr><td><h3 align=\"left\">PS Sono un simpatico umorista. Se mi mandate a cagare, ad esempio, cordialmente io:</h3><table border=\"0\" width=\"157\" height=\"117\"><tbody><tr><td align=\"left\"><p><em>Invitum<br />inviti<br />invito<br />invitum<br />invitum<br />invito </em></p></td><td align=\"left\"><p><em>invita<br />invitorum<br />invitis<br />invita<br />invita<br />invitis</em></p></td></tr></tbody></table>&nbsp;</td><td valign=\"top\"><p>Ultimamente mi son messo a fare dei video su <strong>youTube</strong>, guardate qui:</p><div class=\"video-cell\" style=\"width: 32.6%\">      	<div class=\"video-entry\">     			<div class=\"v120WideEntry\"><div class=\"v120WrapperOuter\"><div class=\"v120WrapperInner\"><a href=\"http://www.youtube.com/watch?v=HksCU5IznKk&amp;feature=channel_page\"><img class=\"vimg120\" src=\"http://i1.ytimg.com/vi/HksCU5IznKk/default.jpg\" alt=\"Camerlengo ultima frontiera\" title=\"Camerlengo ultima frontiera\" /></a><div id=\"quicklist-icon-HksCU5IznKk\" class=\"addtoQL90\"><a href=\"http://www.youtube.com/user/palladiusbonton#\" title=\"Aggiungi video a QuickList\"></a></div></div></div></div><div id=\"video-main-content-HksCU5IznKk\" class=\"video-main-content\"><div class=\"video-facets\">  					<span class=\"video-username\"><a href=\"http://www.youtube.com/user/palladiusbonton\">Ric yellow in goliardia </a></span></div></div></div></div><div class=\"v120WideEntry\"><div class=\"v120WrapperOuter\"><div class=\"v120WrapperInner\"><a href=\"http://www.youtube.com/watch?v=4VBxd9n1dSU&amp;feature=channel_page\"><img class=\"vimg120\" src=\"http://i1.ytimg.com/vi/4VBxd9n1dSU/default.jpg\" alt=\"Piano: Firth of Fifth (Genesis)\" title=\"Piano: Firth of Fifth (Genesis)\" /></a></div></div></div><div id=\"video-main-content-4VBxd9n1dSU\" class=\"video-main-content\"><div class=\"video-title\"><div class=\"video-short-title\">						<a href=\"http://www.youtube.com/watch?v=4VBxd9n1dSU&amp;feature=channel_page\" title=\"Piano: Firth of Fifth (Genesis)\">Here I play a fragment of &#39;Firth of Fifth&#39; (Genesis)</a> 				</div> 				 			</div>  				</div><h4><a href=\"http://www.youtube.com/profile?user=palladiusbonton\" target=\"_blank\" title=\"I miei video su YouTube\">Riccardo on YouTube </a></h4><p>Scrivetemi!!!</p><p><div class=\"mosimage\"  style=\" float: center;\" align=\"center\"><img src=\"http://www.palladius.it/joomlait//images/stories/email/ric-email.png\" width=\"214\" height=\"37\" hspace=\"6\" alt=\"La mia email\" title=\"La mia email\" border=\"0\" /><div class=\"mosimage_caption\" style=\"text-align: center;\" align=\"center\">Email (non cliccabile causa spam, dovete usare le vostre belle ditine e strascriverla a manaus)</div></div>&nbsp;</p><p>&nbsp;</p></td></tr></tbody></table><p><img src=\"http://www.palladius.it/joomlait/images/banners/banner%20pal5.jpg\" alt=\" \" width=\"650\" height=\"98\" /></p>','','1','0','0','0','2004-08-19 20:11:07','62','Riccardo Carlesso','2009-05-01 09:34:57','64','0','0000-00-00 00:00:00','2006-07-17 00:00:00','0000-00-00 00:00:00','email/ric-email.png|center|La mia email|0|Email (non cliccabile causa spam, dovete usare le vostre belle ditine e strascriverla a manaus)|bottom|center|','','menu_image=-1\nitem_title=0\npageclass_sfx=\nback_button=0\nrating=0\nauthor=0\ncreatedate=0\nmodifydate=0\npdf=0\nprint=0\nemail=0','184','0','4','riccardo carlesso palladius bonton glossario dieta poesie foto','la homepage di Riccardo Carlesso, detto anche Palladius BonTon','0','61683','riccardo-carlesso-home');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '46','Come faccio a mettere le foto sul sito?','','<p align=\"justify\">Per un gran colpo di fortuna&nbsp;(la classica botta di culo), sono riuscita a mettere alcune foto su questo sito, <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" />&nbsp;anche se un po&#39; a casaccio... <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-embarassed.gif\" border=\"0\" alt=\"Embarassed\" /></p><p align=\"justify\">Ecco come potete farlo anche voi!!<img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" />&nbsp;<img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" /></p><p align=\"justify\">&nbsp;</p>','<p align=\"justify\">Per prima cosa vi dovete registrare al sito.</p><p align=\"justify\">Quando poi avrete effettuato il login, dal menu utente cliccate su &quot;upload media&quot;, poi sull&#39;icona &quot;media manager&quot;...&nbsp;&nbsp;&nbsp;<img style=\"width: 43px; height: 42px\" src=\"http://www.palladius.it/joomlait/components/com_zoom/www/images/admin/mediamgr.png\" border=\"0\" alt=\"Icona Media Manager in bianco e nero\" title=\"Icona Media Manager\" width=\"43\" height=\"42\" />&nbsp;&nbsp;&nbsp;&nbsp; questa!</p><p align=\"justify\">Se ci andate sopra la vedrete cos&igrave;...&nbsp;&nbsp;&nbsp;<img style=\"width: 45px; height: 42px\" src=\"http://www.palladius.it/joomlait/components/com_zoom/www/images/admin/mediamgr_f2.png\" border=\"0\" alt=\"Icona Media Manager a colori\" title=\"Icona Media Manager\" width=\"45\" height=\"42\" />&nbsp;&nbsp;&nbsp;&nbsp;cliccate!</p><p align=\"justify\">A questo punto vedrete una pagina con&nbsp;l&#39;icona di media manager, e un menu con la freccetta con scritto &quot;pick a gallery&quot;, attraverso il quale selezionare un album. Selezionate l&#39;album di destinazione delle vostre foto, ma se vi sbagliate no problem, tanto poi si pu&ograve; cambiare.<br /></p><p align=\"justify\">Fatto? (oddio, mi sembra Art Attack!&nbsp;<img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-laughing.gif\" border=\"0\" alt=\"Laughing\" width=\"18\" height=\"18\" /> )</p><p align=\"justify\"><br />Cliccando sull&#39;icona grigia con la stellina&nbsp;&nbsp;<img src=\"http://www.palladius.it/joomlait/components/com_zoom/www/images/admin/new.png\" border=\"0\" alt=\"Icona Upload File in bianco e nero\" title=\"Icona Upload File\" width=\"32\" height=\"32\" />&nbsp;&nbsp; (questa), che si trova a destra sotto la scritta con il titolo della cartella e lo spazio in essa utilizzato,&nbsp;dovreste entrare nella pagina vera e propria di upload dei file!!</p><p align=\"justify\">Prima di cliccare su questa icona non ho selezionato nulla, ci sono due menu con freccetta e uno da selezionare, ma l&#39;upload riesce benissimo lo stesso.</p><p align=\"justify\">Cliccate l&#39;icona con la stellina e basta, che a questo punto si colora, <img src=\"http://www.palladius.it/joomlait/components/com_zoom/www/images/admin/new_f2.png\" border=\"0\" alt=\"Icona Upload File a colori\" title=\"Upload file\" width=\"32\" height=\"32\" />&nbsp;quindi nella pagina di upload avrete quattro diverse opzioni per effettuare l&#39;upload dei file (Single/zip file, multiple files, drag n drop, scan directory). Io ho sempre usato le prime due opzioni (file singoli e multipli) ed &egrave; molto semplice fare l&#39;upload, se ci sono riuscita io&nbsp;pu&ograve; riuscirci davvero chiunque...<img src=\"http://www.goliardia.it/joomla/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-laughing.gif\" border=\"0\" alt=\"Laughing\" title=\"Laughing\" width=\"18\" height=\"18\" /><img src=\"http://www.goliardia.it/joomla/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-laughing.gif\" border=\"0\" alt=\"Laughing\" title=\"Laughing\" width=\"18\" height=\"18\" />&nbsp;</p><p align=\"justify\"><strong><u>N.B.:<br /></u></strong>Quando sarete nella pagina di upload potrebbe aprirsi un pop up che propone l&#39;installazione di un programma, se cliccate su &quot;no&quot; o lo chiudete l&#39;upload non sar&agrave; pregiudicato, io ci sono riuscita senza installare nulla sul mio pc.</p><p align=\"justify\"><br /><u><strong>ALCUNE CONSIDERAZIONI PRIMA DI FARE L&#39;UPLOAD:</strong></u></p><p align=\"justify\">Ho appurato che l&#39;upload di file zippati funziona solo mettendo una foto zippata per volta, e non per esempio una cartella zippata con dentro pi&ugrave; foto&nbsp;<img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-frown.gif\" border=\"0\" alt=\"Frown\" width=\"18\" height=\"18\" />&nbsp; ed &egrave; un po&#39; una rottura di scatole...<br />L&#39;upload multiplo invece funziona benissimo, si&nbsp;riescono a &nbsp;mandare fino a 10 foto per volta!!<br />Queste sono solo &quot;istruzioni base&quot; per mettere su i file, per esempio io&nbsp;non ho ancora provato dalla pagina di upload il &quot;Drag n drop&quot; (per&ograve; l&#39;ha provato Pall e&nbsp;a lui&nbsp;non funziona)&nbsp;e &quot;Scan Directory&quot; per via delle mie difficolt&agrave; con l&#39;inglese (oltre che per la mia ignoranza informatica)&nbsp;<img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-embarassed.gif\" border=\"0\" alt=\"Embarassed\" width=\"18\" height=\"18\" /><br />Voi provateci, e vedete cosa succede! <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" width=\"18\" height=\"18\" /></p><p align=\"justify\">&nbsp;</p><p align=\"justify\"><strong><u>ED ECCOCI AL DUNQUE... MANDIAMO LE NOSTRE FOTO!</u></strong></p><p align=\"justify\">Nella&nbsp;sezione di upload sfogliate per cercare il file, esattamente come se doveste mandare un allegato per e-mail,&nbsp;selezionate l&#39;album di destinazione (se per caso vi foste sbagliati a selezionarlo prima),&nbsp;scrivete il titolo&nbsp;della foto (perch&egrave; il titolo che scrivete &quot;a mano&quot; sia abbinato alla foto dovete deselezionare la casella &quot;Set media names with original filenames), poi&nbsp;compilate il campo per l&#39;eventuale descrizione che comparir&agrave; puntando il mouse sulla foto, cliccate su upload, aspettate pochi istanti (un po&#39; di pi&ugrave; se come me non avete l&#39;adsl) e...</p><p align=\"justify\">... FATTO!! <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" width=\"18\" height=\"18\" /><img src=\"http://www.goliardia.it/joomla/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" title=\"Smile\" width=\"18\" height=\"18\" /></p><p align=\"justify\">Se andate nell&#39;album troverete la foto nuova! <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" width=\"18\" height=\"18\" /><img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-smile.gif\" border=\"0\" alt=\"Smile\" width=\"18\" height=\"18\" /></p><p align=\"justify\">&nbsp;</p>','1','2','0','25','2006-02-14 01:17:31','76','','2006-09-14 11:54:23','64','0','0000-00-00 00:00:00','2006-02-14 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','12','0','5','','','1','153','come-faccio-a-mettere-le-foto-sul-sito');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '6','Barzellette Matematiche','Barzellette Matematiche','<p>Seguono alcune chicche che hanno a che fare con la matematica. Solo gl&#39;ingegneri ridono (&egrave; testato).</p>','<font size=\"+1\">&#39;</font>Telecom Italia, informazione gratuita... il numero da lei selezionato &egrave; immaginario, si prega di riagganciare, ruotare di 90&deg; l&#39;apparecchio e riprovare&#39; <div align=\"right\"><em>(questa &egrave; difficilotta)</em></div>   <hr width=\"30%\" />Teorema. Un gatto ha nove code. <br />Dimostrazione. Nessun gatto ha otto code. Un gatto ha una coda in pi&ugrave; di nessun gatto. Quindi un gatto ha nove code. <br />  <hr width=\"30%\" /> <br />Perche&#39; le donne sono brave in matematica? Perche&#39; in un&#39;equazione prendono un membro, lo mettono tra parentesi, lo elevano alla massima potenza e infine lo riducono ai minimi termini! <br />  <hr width=\"30%\" /> <br />Secondo una recente statistica, l&#39;80% degl&#39;Italiani NON sa la matematica... pensate, quasi la met&agrave;!  <div align=\"right\"><em>(Daniele Luttazzi))</em></div>   <hr width=\"30%\" /> <br />Cosa ci fa un matematico da solo al ristorante che discute animatamente  con la sua insalata?!?    <div align=\"right\">(detta le condizioni al contorno)</div>    <hr width=\"30%\" />   <p>Ges&ugrave; davanti a una folla di credenti si leva in alto e comincia a recitare: &#39;y=ax2+bx+c...&#39; Uno della folla esclama: &#39;Ecco, ci risiamo, la solita parabola... <br /> </p> <hr width=\"30%\" /> <br />&#39;Siamo a una festa di fine anno dei logaritmi; tutti ballano e si divertono in discoteca, pure sen(x) e cos(x) (la tangente no perch&eacute; &egrave; un po&#39; montata...) hanno accantonato per una volta le inimicizie e ballano allegramente sui cubi, quand&#39;ecco che si nota, in un cantuccio, un povero ex che piange solo soletto. Ecco che ln(x), il capo dei logaritmi (log10(x) &egrave; stato cacciato perch&eacute; s&#39;incasinava con le derivate), va da lui e gli dice: &#39;Su, vieni a ballare, unisciti a noi...&#39; e lui: &#39;No, no...&#39;; &#39;Ma su, dai, integrati!&#39; e l&#39;altro: &#39;Tanto cosa cambia?&#39; <br />NEW! <br />Ne ho trovato un&#39;interessante variante (da raccontare al posto dell&#39;altra, ovviamente): <br />Un ragazzo sale su un autobus e comincia a spaventare tutti: &quot;Ti integrer&ograve;! Ti deriver&ograve;!&quot; Cos&igrave; ognuno scappa via impaurito. Solo una persona rimane. Il ragazzo lo raggiunge e gli dice: &quot;Non hai paura, ti integrer&ograve;, ti derivr&ograve;!!!&quot; E l&#39;altro dice: &quot;Non ho paura, io sono e alla x.&quot; <br />  <hr width=\"30%\" />- Babbo, babbo, mi faresti il compito di matematica stasera? <br />- No, figliolo, non sarebbe corretto.<br />- Beh, puoi sempre provare. <br />    <hr width=\"30%\" /> <br />Per 3 punti non allineati passa una ed una sola retta, purch&eacute;  abbastanza spessa.    <div align=\"right\"><em>(Sentita dal professor Barozzi)</em></div>   <hr width=\"30%\" /> <br />La trigonometria serve soprattutto a 3 categorie di persone: ai cartografi, ai naviganti (ed astronomi), ma soprattutto agli scrittori di libri di trigonometria.  <div align=\"right\"><em>(Sentita dal professor Barozzi)</em></div>   <hr width=\"30%\" /> <br />Qualunque teorema tu abbia dimostrato esiste sempre un giapponese  piccolo ad arbitrio che l&#39;ha trovato prima di te.    <div align=\"right\"><em>(Sentita dal professor Barozzi)</em></div><p>&nbsp;</p><hr width=\"30%\" />  Indovinello: <p>&nbsp;</p><p><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lim&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; sen x <br />&nbsp;&nbsp;&nbsp; <em>n --&gt; inf.</em> &nbsp; ------&nbsp; =&nbsp; sex<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; n  </p><p>Traccia: elidi le n sopra    e sotto (&egrave; lecito in quanto n tende a infinito).&nbsp;  </p> <hr width=\"30%\" /> <br />Il colmo per un matematico?  <div align=\"right\">(Avere una pianta con le radici quadrate)</div>   <div align=\"right\">(passatami da Marco Budinich; riveduta e corretta dal figlio)</div>   <hr width=\"30%\" /> <br />Cos&#39;&egrave; una citt&agrave; compatta?  <div align=\"right\">(E&#39; una citt&agrave; che pu&ograve; essere controllata da un numero arbitrariamente grande, ma finito, di poliziotti miopi) <hr width=\"30%\" /></div>  Qual &egrave; l&#39;integrale di circuitazione complessa intorno all&#39;Europa?!?  <div align=\"right\">(E&#39; nullo, infatti tutti i poli sono esterni ad essa)</div>   <div align=\"right\">(nota politica: A dir la verit&agrave;, ci    sarebbero dei poli, ma questi sono eliminabili!)</div>    <hr width=\"30%\" />  La top 20 di come preferiamo farlo... <br />1) Galois lo faceva la notte prima. <br />2) Moebius lo faceva sempre dalla stessa parte. <br />3) Gli algebristi lo fanno in gruppo. <br />4) I combinatori lo fanno in tutti i modi possibili. <br />5) I matematici non lo fanno: lo lasciano come facile esercizio al lettore. <br />6) I fisici matematici capiscono la teoria di come si fa, ma hanno difficolt&agrave; per ottenere risultati pratici. <br />7) Markov lo fa incatenato. <br />8) I veri analisti lo fanno quasi ovunque. <br />9) Gli statistici probabilmente non lo fanno. <br />10) I fisici quantistici possono sapere quanto veloce vanno, o dove lo fanno, ma non entrambe le cose. <br />11) [I logici lo fanno] o [non (i logici lo fanno)]. <br />12) Gl&#39;informatici lo fanno a partire dal pi&ugrave; basso (depth-first, poco traducibile...). <br />13) Fermat cerc&ograve; di farlo nel margine, ma non ci stava dentro. <br />14) Gli aerodinamici lo fanno raccolti. <br />15) I cosmologi lo fanno nei primi 3 minuti. <br />16) I teorici dei gruppi lo fanno con Il Mostro. (?) <br />17) I matematici puri lo fanno con rigore. <br />18) I programmatori di C lo fanno con i puntatori long. <br />19) I topologi lo fanno apertamente. <br />20) Gli elettronici lo fanno anche fuori fase. <br />  <hr width=\"30%\" /> <br />Un promettente laureando in fisica sta presentando la sua tesi di laurea. Procede con calcoli e derivate varie, finch&eacute; non ottiene qualcosa tipo: <br />&nbsp;&nbsp;&nbsp; F = - m a <br />Questi &egrave; visibilmente imbarazzato, e cos&igrave; il suo supervisore, e idem il resto della commissione. Lo studente tossisce nervosamente e dice: &quot;Sembra che io abbia fatto un piccolo errore da qualche parte.&quot; <br />Uno dei matematici della commissione replica asciutto: &quot;O questo o un numero dispari di essi!&quot; <br />  <hr width=\"30%\" /> <br />Due matematici sono al bar. Il primo dice al secondo che la persona media sa ben poco di matematica di base. Il secondo &egrave; in disaccordo, e dice che la maggior parte della gente pu&ograve; cavarsela con una quantit&agrave; ragionevole di problemi. Il primo se ne va al bagno, e in sua assenza il secondo chiama la cameroiera. Le dice che tra pochi minuti, quando sar&agrave; tornato il suo amico, la richiamer&agrave; e le porr&agrave; una domanda. Tutto quello che lei dovr&agrave; fare sar&agrave; rispondere un terzo x cubo. Lei ripete: &#39;un ter ... il cubo?&#39; Lui ripete: &#39;un terzo x cubo&#39;. Lei: &#39;un ter zicscubo&#39; S&igrave;, va bene, dice lui. Cos&igrave; lei annuisce, e se ne va mugugnando tra s&eacute;, &#39;un ter zicscubo, un ter zicscubo...&#39;. <br />Il primo torna e il secondo propone una scommessa per provare la sua tesi, che la maggior parte della gente sa qualcosa di matematica. Dice che chieder&agrave; alla bionda cameriera un integrale, e il primo ridendo annuisce. <br />Il secondo uomo la chiama e le chiede &#39;qual &egrave; l&#39;integrale di x quadro?&#39;. <br />La cameriera dice &#39;un terzo x cubo&#39; e, mentre se ne va, si volta e dice &#39;pi&ugrave; una costante&#39;! <br />  <hr width=\"30%\" /> <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; lim&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; --- <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8-&gt;9&nbsp;&nbsp; \\/ 8&nbsp;&nbsp; = 3 <br />  <hr width=\"30%\" /> <br />Qual e&#39; l&#39;unica cosa che spaventa davvero uno spettro?? L&#39;antitrasformata di Fourier! <br />  <hr width=\"30%\" /> <br />1 + 1 = 3, per grandi valori di 1 <br />  <hr width=\"30%\" /> <br />Un ingegnere, un fisico e un matematico sono rinchiusi ognuno in una stanza con una scorta di scatolette di cibo, ma senza apriscatole. Dopo un mese le porte delle tre stanze vengono aperte. L&#39;ingegnere e&#39; bello grasso e&nbsp; tutte le scatolette sono state aperte. Alla richiestadi come abbia fatto, risponde che ha usato la fibbia della cintura perfarsi un apriscatole. Anche il fisico se l&#39;e&#39; cavata: circa la meta&#39; delle scatolette e&#39; stata aperta. La sua risposta alle domande dei ricercatori e&#39; un lungo panegirico a base di &quot;Applicando il vettore forza alla...&quot;, &quot;considerando l&#39;energia potenziale del contenuto dellascatoletta...&quot;. Comunque in realta&#39; le scatolette sono state aperte a <br />furia di botte sul muro. Infine e&#39; aperta la porta della stanza del matematico: questi e&#39; paurosamente magro e denutrito; tutte le scatolette sono chiuse. Il poveraccio ne tiene una nella mano rinsecchita e la regge a pochi centimetri dal viso, la fissa con occhi spiritati e mormora: &quot;Supponendo, per assurdo, che sia aperta...&quot;. <br />  <hr width=\"30%\" /> <br />Due uomini su una mongolfiera si perdono nel deserto del Sahara. Vedono un vecchio all&#39;ombra di una palma e scendono a chiedere informazioni: &quot;Dove siamo?&quot;. Dopo lunghi secondi di riflessione il vecchio risponde: &quot;Su un pallone&quot;. &quot;Grazie, signor matematico&quot;. Il vecchio, meravigliato, chiede: &quot;Ma come avete saputo che io sono un matematico?&quot;. &quot;Per tre ragioni: la prima perche&#39; avete riflettuto a lungo prima di rispondere; la seconda perche&#39; la vostra risposta e&#39; stata molto precisa; la terza perche&#39; &egrave; assolutamente inutile!&quot;. <br />  <hr width=\"30%\" /> <br />Recenti studi sulla sessualita&#39; femminile hanno determinatoquattro tipi di donne. Esistono: la donna asmatica, quella religiosa, quella matematica e quella assassina. L&#39;asmatica fa (inspirando): <br />&quot;Ahhhh ahhhhhhh ahh ahhhhhhhhhhh&quot;. La religiosa: &quot;Dio... Dio... Dioooo mio....&quot;. La matematica: &quot;Di piu&#39;... di piu&#39; di piu&#39; di piu&#39;!!!&quot;. L&#39;assassina: &quot;Se lo tiri via ti ammazzo!&quot;. <br />  <hr width=\"30%\" /> <br />Sapete perche&#39; i matematici non fanno l&#39;amore ? Perche&#39; aggiungendo e togliendo una stessa quantita&#39; non cambia nulla !!! <br />  <hr width=\"30%\" /> <br />Cornuto matematico: il cornuto e&#39; uno (intero) a cui un terzo ha sottratto la meta&#39; (1=1/3-1/2). <br />  <hr width=\"30%\" /> <br />Il fisico, l&#39;ingegnere e il matematico vengono chiusi in una stanza per un test attitudinale, con una scatoletta di carne. Per farla breve, il fisico la apre con una soluzione &quot;fisica&quot;, ovviamente, tipo <br />usare la sedia per creare una leva ed aprirla, il matematico calcola un angolo di rimbalzo che alteri la struttura molecolare della latta... entrambi sono comunque fisicamente provati dall&#39;impegno che <br />hanno messo nella risoluzione del problema. L&#39;ingegnere e&#39; tranquillissimo, invece, e ha fatto colazione con la sua scatoletta. &quot;Ma... come ha fatto?&quot; chiedono i tecnici allibiti. &quot;Oh, niente&quot;, fa <br />lui con nonchalance, &quot;tengo sempre in tasca un coltellino svizzero per questi test del cazzo!&quot;. <br />  <hr width=\"30%\" /> <br /><strong>Definizione 1.1</strong> -x:=gay; <br /><strong>Proposizione 1.1 </strong>- x come sopra:  <ul> <ul>PEROGNI x E! donna libera in +&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (1.1)</ul>Dimostrazione - La lasciamo come facile esercizio al lettore. </ul><strong>  Corollario 1.2</strong> - (Principio di utilit&agrave; dei Gay).  <ul> <ul>W i x!</ul>Dimostrazione - Discende direttamente dalla (1.1). </ul>   <div align=\"right\">(trovata in aula 3.2 di Ingegneria a Bologna)</div>   <hr width=\"30%\" />Problema - Calcolare:&nbsp;  &ograve;fantino dcavallo <br />Soluzione -Poich&eacute; fantino = uno su cavallo, otteniamo una banale integrazione, donde: <br /><strong>&int;</strong>fantino dcavallo&nbsp; = ln|Cavallo|+C  <div align=\"right\">(sentita da Mattia Preti, alias Pelo, quando trovo l&#39;email ve la passo)</div>','1','3','0','1','2004-07-07 11:54:06','62','','2007-12-24 15:02:06','64','0','0000-00-00 00:00:00','2004-07-07 00:00:00','0000-00-00 00:00:00','food/coffee.jpg|left||0\r\nfood/bread.jpg|right||0','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','4','0','10','','','0','619','barzellette-matematiche');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '10','Il ponte e i 4 attraversatori','Il ponte e i 4 attraversatori','Ci sono quattro persone che devono attraversare un ponte e possiedono una torcia. E\' buio e molto stretto quindi il ponte pu&ograve; essere attraversato al piu\' da due persone e comuqnue occorre sempre avere la torcia con se\'. Sapendo che queste quattro persone sono un velocista, un atleta, una persona normale e una obesa e quindi impiegano rispettivamente 1,2,5 e 10 minuti per attraversare il ponte (e che ovviamente la velocita\' di una coppia e\' uguale alla velocita\' del piu\' lento), qual e\' il tempo minimo che occorre al gruppo per passare il ponte?!? (Attenzione, <em>non</em> &egrave; 19, se no mi licenziereste subito!)  ','','1','3','0','14','2004-05-12 11:54:06','62','','2006-02-15 11:03:42','64','0','0000-00-00 00:00:00','2004-01-01 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','4','0','7','','','0','709','il-ponte-e-i-4-attraversatori');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '11','Operatore Carlessiano','Operatore Carlessiano',' <p>Dopo tante lezioni sulle trasformate di Fourier e Laplace, dettate da un&#39;intuizione pi&ugrave; che lecita (i coefficienti dei termini di Taylor e di Fourier sono vagamente simili), ho inventato l&#39;<strong>operatore trans-trasformazionale di Carlesso</strong> (o pi&ugrave; semplicemente meta-trasformazionale, in un secondo momento decider&ograve; se serva effettivamente a qualcosa<a name=\"nota-trasformata\" title=\"nota-trasformata\"></a>!):</p><p>Carl[f(x)](<font face=\"Symbol\">x</font>) = g(<font face=\"Symbol\">x</font>) </p><p>cos&igrave; definito:<br />      Sia f(x) sviluppabile in serie di Fourier: <strong>f(x)=a<sub><font size=\"-2\">0</font></sub>+a<sub><font size=\"-2\">1</font></sub>cos(x)+b<sub><font size=\"-2\">1</font></sub>sen(x)+...+a<sub>n</sub>cos(nx)+b<sub><font size=\"-2\">n</font></sub>sen(nx) </strong><br />    (Non che sia necessariamente necessario, almeno secondo me, ma per ora semplifica tutto). Definisco l&#39;operatore funzionale <strong>Carl </strong>come agente sulle seguenti funzioni<br />       </p>     <pre>1 --&gt; 1<br />cos(x) --&gt; <font face=\"Symbol\">x</font><br />sen(x) --&gt; i<font face=\"Symbol\">x</font><br />  ... <br />cos(nx) --&gt; <font face=\"Symbol\">x</font><sup>n</sup><br />sen(nx) --&gt; i<font face=\"Symbol\">x</font><sup>n</sup>   <br /></pre>  <p> Ecco che cos&igrave; ottengo da una serie di potenze una serie di Taylor (eventualmente convergente: purtroppo non ne ho la certezza); ho una trasformata che mangia una funzione da R in R e sputa fuori una funzione da R in C. Essa &egrave; ovviamente lineare (eccheccavolo!) ma purtroppo non ha propriet&agrave; &#39;belle&#39; come le trasformate di Laplace e Fourier per le derivate. Si ottengono per&ograve; trasformate interessanti: funzioni insospettabili si trasformano in logaritmi o arcotangenti! (La settimana prossima se riesco scrivo alcune trasformate notevoli).</p>','','1','1','0','15','2004-05-12 11:54:06','62','Riccardo Carlesso','2007-05-27 21:37:54','64','0','0000-00-00 00:00:00','2004-01-01 00:00:00','0000-00-00 00:00:00','fruit/cherry.jpg|left||0\r\nfruit/peas.jpg|right||0\r\nfood/milk.jpg|left||0','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','5','0','8','operatore carlessiano, laplace, fourier','Operatore trans-trasformazionale: un operatore che trasforma trasformate in altre trasformate :-)','0','502','operatore-carlessiano');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '12','Chi sono','Chi sono','<p><img src=\"http://www.palladius.it/joomlait/images/stories/ric-lavoro2.jpg\" alt=\" \" width=\"90\" height=\"125\" align=\"right\" /><img src=\"http://www.palladius.it/joomlait/images/riccardo/pal-appartamento.jpg\" alt=\"Riccardo (bellissimo) nella casa a Bologna\" title=\"Riccardo nella casa a Bologna\" width=\"187\" height=\"125\" align=\"right\" /><span class=\"ttoso\"><strong>Storia</strong></span>: mi chiamo <strong>Riccardo <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=87&amp;Itemid=26\">Carlesso</a></strong>, nato un lontano pomeriggio (alle 18:50) del 29 dicembre &#39;76 (capricorno ascendente leone<sup>1</sup>, anche se non ci credo! Comunque grazie, Beatrice) ad Argenta. Mi sono elementato ad Argenta, mediato ad Argenta, diplomato ad Argenta e in una brusca, tragica, e imprevedibile rottura col passato mi sono laureato a Bologna in Ingegneria Informatica nel lontano marzo 2002 (<a href=\"http://www.palladius.it/carlesso/img/2002/laurea/foto.html\" target=\"_blank\">qui ci sono le foto</a>), con una tesi sulla Videosorveglianza (<a href=\"http://www.palladius.it/joomlait/images/zoom/Docs/tesi-carlesso.pdf\">pdf</a>). Ho lavorato 6 mesi presso il DEIS (all&#39;Universit&agrave;) continuando sostanzialmente il lavoro della tesi. Ho fatto un master a Parma sulla Sicurezza informatica nel 2003 e come stage sono stato preso in un&#39;azienda di telecomunicazioni (<a href=\"http://www.labs.it/\">Laboratori Guglielmo Marconi</a>), a Bologna, per la quale lavoro da settembre 2003 occupandomi di Network Design (reti di computer, per i non addetti ai lavori). Da ottobre 2004, <strong>vivo a Bologna</strong> in via S. Isaia.</p><p align=\"left\"><br /><img src=\"http://www.palladius.it/joomlait/images/riccardo/argenta.gif\" alt=\" \" title=\"Argenta\" width=\"92\" height=\"93\" align=\"left\" /><span class=\"ttoso\"><strong>G</strong><strong>eografia</strong></span>: sono nato ad <strong>Argenta</strong>, ridente cittadina nel ferrarese. Da 10 anni approssimativamente vivo a Bologna (a parte un anno a casa e uno a Parma), e ho scoperto che quella zona i bolognesi la chiamano &#39;<em>bassa ferrarese</em>&#39;. Pensavo solo per motivi di metri rispetto al livello del mare (mi pare che siamo intorno ai -7, e come dice Ridge &#39;<em>un romagnolo si accorge quando passa il ponte della Bastia: 3 metrio a salire e 20 a scendere!</em>&#39;), ma visto il tono penso ci sia anche dell&#39;altro.  <img src=\"http://www.palladius.it/joomlait/mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-wink.gif\" border=\"0\" alt=\"Wink\" title=\"Wink\" /> </p><p align=\"left\"><br />   <span class=\"ttoso\"><img src=\"http://www.palladius.it/joomlait/images/riccardo/hitch.jpg\" alt=\" \" title=\"Guida galattica\" width=\"56\" height=\"89\" align=\"right\" /><strong>Letteratura</strong></span>: amo leggere quando vado a far pup&ugrave; o prima di dormire (buffamente, per&ograve;, mi rende pi&ugrave; difficile alzarmi l&#39;indomani, quindi ormai leggo solo in bagno e in treno...). I miei autori preferiti sono <strong>Douglas Adams</strong> e Benni, i libri di matematica e logica, di fantasy (expecially DragonLance, Tolkien, Pratchett), pi&ugrave; qualche libro sparuto: Il Conte di Montecristo di Dumas, Godel Escher e Bach (geniale). Ho scritto poesie a met&agrave; liceo, due in latino, altre in italiano, vi consiglio di andarle a leggere alla sezione <a href=\"http://www.palladius.it/poesiepure.php\">poesie</a> poich&egrave; l&#39;idea alla base &egrave;    abbastanza rara a trovarsi (non &egrave; nuova, ma non usa da secoli ormai). Ultimamente sto leggendo la Storia d&#39;Italia di Indro Montanelli, che mi sta facendo amare ferocemente la Toscana.. Su ci&ograve; che amo leggere mi sa che potete trovare un <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=82&amp;Itemid=26\">articoletto</a>&nbsp; apposta.<br /></p><p align=\"left\">   <img src=\"http://www.palladius.it/joomlait/images/riccardo/ali_g_indahouse_200_51805a.jpg\" alt=\"Ali G Indahouse\" title=\"Ali G Indahouse\" width=\"66\" height=\"100\" align=\"left\" /><img src=\"http://www.palladius.it/joomlait/images/riccardo/cristo-compagnone.jpg\" alt=\"Il Cristo Compagnone di &#39;Dogma&#39;\" title=\"Il Cristo Compagnone di &#39;Dogma&#39;\" width=\"240\" height=\"100\" align=\"left\" /><img src=\"http://www.palladius.it/joomlait/images/riccardo/saint%20seiya%20-%20hades%2022%20shared%20by%20mac%20fly.jpg\" alt=\"I Cavalieri dello Zodiaco\" title=\"I Cavalieri dello Zodiaco\" width=\"133\" height=\"100\" align=\"left\" /><span class=\"ttoso\"><u><strong> </strong></u><strong>Sp</strong><strong>ettacolo</strong></span>: amo il cinema impegnato. Sono pieno di divx e DVD che amo vedere con gli amici. Film culto sono per me:<strong> Ali G Indahouse</strong> (<em>RESPECT! Buyakasha!</em>), tutti i film di <strong>Kevin Smith</strong> (<em>Snootchie Bootchie!!!</em>), Frankenstein Junior (<em>could be worse: could be rainin&#39;</em>), Animalhouse (<em>was it over when    the germans bombed Pearl Harbor? Hell not! And it ain&#39;t over now!</em>), i Blues    Brothers (<em>Well thank you pal, the day I get out of prison, my own brother    picks me up in a police car</em>), Wayne&#39;s world (<em>ora    far&ograve; il riff &quot;Scusi posso aiutarla?&quot;</em>),&nbsp; Fatti    strafatti e strafighe (<em>fico! forte! E poooooooi?!?</em>). Amo anche i cartoni animati, in particolare ho tutte le puntate di: Dungeons &amp; Dragons, <strong>I Cavalieri dello Zodiaco</strong> (Saint Seiya), E&#39; quasi magia Johnny (Kimagure Orange road) e Il mistero della pietra azzurra (Nadia - The secret of blue water).<br /></p><div align=\"left\"><img src=\"http://www.palladius.it/joomlait/images/riccardo/dscn2327.jpg\" alt=\"Ric e il pinguino\" title=\"Ric e il pinguino\" width=\"90\" height=\"120\" align=\"right\" /><strong><span class=\"ttoso\">Scienze</span>: </strong>amo tanto la matematica. E un po&#39; la fisica (in quasi un anno sono arrivato a met&agrave; di &quot;Il tao e la Fisica&quot;), le lingue in generale (per le quali, vi giuro, sono molto dotato, almeno finch&eacute; sto zitto, dicono). Amo l&#39;informatica (simpatizzo per linux, adoro shell, righe di comando e <strong>pinguini</strong> e ho loro dedicato una sezioncina su questo sito), programmare, filosofare sull&#39;X-naturalismo delle cose e sul <em>meta</em> (eredit&agrave; di Hoefstadter). Per motivi lavorativi amo le reti, la parte che mi diverte di pi&ugrave; sono i tripli tunnel <em>ssh </em>con&nbsp; scappellamento a destra, la remotizzazione e le VPN&nbsp; tra casa ufficio e&nbsp; casa degli amici.  <br /></div><p align=\"center\"><span class=\"ttoso\"><br /></span>   </p><div align=\"left\"><span class=\"ttoso\"><strong>Hobbies</strong> <strong>&amp; sport</strong></span>: strimpello al <strong>piano </strong>(sono conosciuto da tutti per il mitico McGyver), ascolto <strong>musica</strong> (ogni tanto faccio il dj col mio amico Gigi), gioco a <strong><a href=\"http://www.palladius.it/magic.html\">Magic</a>   </strong> (l&#39;ambulanza), scrivo <a href=\"http://www.palladius.it/matematica.html\">teoremini matematici</a>. In varie fasi della mia vita ho giocato di ruolo (D&amp;D, GURPS, Warhammer). Quando poi Gino (la mia panza) cresce troppo e per far pip&igrave; (d&#39;in piedi) devo andare a <em>usta</em> anzich&eacute; a vista, mi metto a nuotare qualche mese per perdere peso (paradossalmente, Gino ringrazia); attualmente sto nuotando e correndo come un disperato, e sono a dieta. Ad oggi ho perso 12 kg in 4 mesi!!! Adoro guardare film la sera (con amici o anche da solo).<br /></div><table border=\"0\" align=\"center\"><tbody><tr><td align=\"center\"><img lang=\"IT\" longdesc=\"http://www.palladius.it/joomlait/Riccardo cge gioca a magic\" src=\"http://www.palladius.it/joomlait/images/riccardo/dscn1915.jpg\" alt=\"magic\" title=\"magic\" width=\"112\" height=\"150\" /> </td><td align=\"center\"><img src=\"http://www.palladius.it/joomlait/images/riccardo/dscn1999.jpg\" alt=\"Ric che nuota\" title=\"Ric che nuota\" width=\"200\" height=\"150\" /></td><td align=\"center\">&nbsp;<img lang=\"IT\" longdesc=\"http://www.palladius.it/joomlait/Riccardo cge gioca a magic\" src=\"http://www.palladius.it/joomlait/images/riccardo/dcp_0223.jpg\" alt=\"Ric al piano\" title=\"Ric al piano\" width=\"225\" height=\"150\" /></td></tr><tr><td align=\"center\"><strong>&nbsp;&nbsp; Ric gioca a magic</strong></td><td align=\"center\"><p><strong>&nbsp;Ric che nuota</strong><br /></p></td><td align=\"center\"><strong>Ric al piano (in un raro virtuosismo informatico)&nbsp;</strong></td></tr></tbody></table><br />Ah beh, poi nel frattempo studio e trovo anche il tempo di    <a href=\"http://www.palladius.it/glossario.html#Intort%C3%A0re\">intortare</a> tutte le ragazze presenti a Bologna (<em>se sei una ragazza maggiorenne, questa &egrave; la mia e-mail: <strong>palladius</strong> chicciolina <strong>email.it</strong>)</em>. Tra le altre cose, sto redigendo una <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=69&amp;Itemid=26\">lista di cose che amo e odio</a>, e dei miei <a href=\"http://www.palladius.it/joomlait/index.php?option=com_content&amp;task=view&amp;id=68&amp;Itemid=26\">gusti culinari</a>.<p>&nbsp;</p><p>&nbsp;</p><div style=\"text-align: center\"><img src=\"http://www.palladius.it/carlesso/img/nota4.jpg\" alt=\" Nota sul diario delle medie\" title=\" Nota sul diario delle medie\" width=\"336\" height=\"120\" /></div><div style=\"text-align: center\"><strong>Una nota sul diario presa dal Carlesso in seconda media</strong>&nbsp;</div><div style=\"text-align: center\">&nbsp;</div><div class=\"RTE small smalldark\" align=\"left\">&nbsp;<span class=\"smalldark\">(<sup>1</sup>): ASCENDENTE IN LEONE.</span> L&#39;ascendente Leone &egrave; legato al sole: emana forza, potere, si deve distinguere mostrando al mondo il suo essere unico e speciale. Facilmente esagera perch&egrave; si considera un eroe a cui nulla &egrave; precluso. Leale, coraggioso e generoso, elargisce vitalit&agrave;, energia e calore a chi gli sta intorno, ma si aspetta sempre qualcosa di ritorno: adulazione, gratitudine, riconoscimenti. L&#39;ascendente Leone si muove sotto le luci della ribalta, lui sotto i riflettori del palcoscenico e davanti la platea silenziosa, in ombra. Ha bisogno di essere visto, che gli si riconoscano delle qualit&agrave;, meglio se le ha, altrimenti le reciter&agrave;. Questo ascendente comincer&agrave; a dare il meglio di se, quando smetter&agrave; di aspettarsi qualcosa in cambio per quello che dona, quando sar&agrave; generoso, leale o altro, non per mostrare al mondo la sua superirit&agrave;, ma perch&egrave; &egrave; cos&igrave;. Fisicamente l&#39;ascendente Leone dona un portamento fiero e occhi dallo sguardo brillante.</div><div style=\"text-align: center\">&nbsp;</div>','','1','0','0','0','2006-01-01 23:49:33','62','Riccardo Carlesso','2006-09-17 19:12:53','64','0','0000-00-00 00:00:00','2006-01-01 00:00:00','0000-00-00 00:00:00','ric-lavoro2.jpg','','menu_image=-1\nitem_title=1\npageclass_sfx=\nback_button=\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=0\nprint=0\nemail=0','33','0','1','Riccardo Carlesso vita curriculum storia','La vita e la storia di Riccardo Carlesso','0','71001','chi-sono');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '13','Sono in ferie!!!','Sono in ferie!!!','<p>Sono in ferie dall\'1 all\'8 di gennaio!!!</p><p>Qualcuno ha proposte vacanziere da farmi?!?&nbsp;</p>','','-1','2','0','3','2006-01-02 14:42:12','62','','2006-01-09 16:04:23','0','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','5','','','0','28','sono-in-ferie');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '14','1. Un gatto','1. Un gatto','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, I)</font></div>  &nbsp;&nbsp;&nbsp; Una delle prime poesie (e prima del canzoniere) di Riccardo Carlesso, uno dei poeti che pi&ugrave; ha caratterizzato la letteratura del fine Novecento, &egrave; senza dubbio molto spensierata e gioiosa. Grazie ai settenari, forme metriche tra le pi&ugrave; veloci, &egrave; riuscito a dare un ritmo particolare a questa poesia, che diviene quasi musicale. Il primo posto dato a questo sonetto nel Canzoniere proverebbe un profondo legame affettivo tra i due. E\' un sonetto sperimentale gi&agrave; a partire dalla metrica: le rime sono invertite e stravolte. Protagonista del sonetto &egrave; un certo gatto, probabile metafora, secondo le fonti, con cui soleva chiamare un suo amico, Alessandro Buzzoni. Altri critici attesterebbero il nome, invece, a Jacopo Martini, altro amico del Carlesso. Il <em>cursus</em> inizia lentamente, e diviene via via pi&ugrave; veloce. Le rime lo assecondano: da un iniziale ABAB, si passa ad un sempre pi&ugrave; frenetico e ridondante GGG. Importante &egrave; poi il termine &quot;gattata&quot;: l\' autore ne parla come di una cosa fatta con furbizia ma che si rivela un fallimento: cosa che quindi si dimostra non cos&igrave; furba. Secondo le fonti dell\' epoca, un fatto simile era stato fatto da Martini (cosa che sposterebbe le ipotesi sulla metafora del gatto da Buzzoni a lui). Sembra che fosse arrivato a scuola in ritardo per evitare un\' interrogazione e che si fosse messo ad origliare per ascoltare quando il professore finiva d\' interrogare. Udito che si sarebbe andati avanti a spiegare, sarebbe entrato per poi essere prontamente interrogato dal professore (o <em>prof</em>, come nello slang degli anni 80-90). Altri esempi (meno celeberrimi) di gattata sarebbero un<em> fuoco</em> (vedi note per il significato) di un certo Jonata Costa, visto dal suo professore di matematica e dalla sua preside, mentre aspettava un suo amico dopo l\' entrata da scuola. <br /> &nbsp;&nbsp;&nbsp; Senz\' altro composto in et&agrave; giovanile, rimane uno dei pi&ugrave; importanti sonetti del Carlesso, ove si nota una certa erudizione ed un sapere ancora troppo enciclopedico (ovvero nozionistico). <br /> &nbsp; <ul> &nbsp;&nbsp;&nbsp; Ier vidi un certo gatto, <br />Iocava co\' compagni<sup><font size=\"-2\">1</font></sup><br />Felice e tutto matto, <br />Non fe\' di che mi lagni<sup><font size=\"-2\">2</font></sup>. <p>&nbsp;&nbsp;&nbsp; Quo modo<sup><font size=\"-2\">3</font></sup> l\' appellammo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <br />Ch&egrave;<sup><font size=\"-2\">4</font></sup> fu felino e scaltro, <br />Ma pi&ugrave; per qualcos\' altro: <br />Gattata la chiamammo. </p><p>&nbsp;&nbsp;&nbsp; E\' azion di gran furbizia <br />Che infin invece sfuma:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 <br />Si ve\' ch\' era fittizia<sup><font size=\"-2\">5</font></sup>. </p><p>&nbsp;&nbsp;&nbsp; In fuoco fu beccato<sup><font size=\"-2\">6</font></sup>, <br />Non pronto interrogato<sup><font size=\"-2\">7</font></sup>, <br />Infine rimandato<sup><font size=\"-2\">8</font></sup>. <br />&nbsp;</p> </ul>   <hr width=\"100%\" /><font size=\"-1\"><strong>1 &quot;Iocava co\' compagni&quot;</strong>: Da notare l\' assonanza: ca, co, co.</font> <br /> <font size=\"-1\"><strong>2 &quot;Non fe\' di che mi lagni&quot;</strong>: Controversa l\' interpretazione di questo verso; probabilmente: &quot;Non aveva mai fatto qualcosa di cui mi dovessi dispiacere&quot;. Questo verso &egrave; l\' unico indizio della grande amicizia che doveva esserci tra l\' autore e &quot;il gatto&quot;.</font> <br /> <font size=\"-1\"><strong>3 &quot;Quo modo&quot;</strong>: &quot;In questo modo&quot;; da notare la reminiscenza latineggiante.</font> <br /> <font size=\"-1\"><strong>4 &quot;Ch&egrave;&quot;</strong>: &quot;poich&eacute;&quot;.</font> <br /> <font size=\"-1\"><strong>5 &quot;Si ve\' ch\' era fittizia&quot;</strong>: &quot;Da ci&ograve; si deduce che doveva non essere vera&quot;. &quot;Si ve\'&quot; &egrave; la contrazione di &quot;si vede&quot;.</font> <br /> <font size=\"-1\"><strong>6 &quot;In fuoco fu beccato&quot;</strong>: &quot;fu colto mentre faceva fuoco&quot;. &quot;Fuoco&quot; fa parte dello slang di fine Novecento molto usato dagli studenti. Vuol dire marinare la scuola.</font> <br /> <font size=\"-1\"><strong>7 &quot;Non pronto interrogato&quot;</strong>: &quot;interrogato quando non era pronto&quot;. Controversa anche l\' interpretazione di questo verso. Non si sa se il Carlesso alludesse al fatto che Alessandro Buzzoni non studiasse mai o al fatto che fosse interrogato sempre quando, di rado, non si era preparato. Per la profonda vicinanza tra il Carlesso e l\' amico, si &egrave; pi&ugrave; propensi ad accogliere la seconda ipotesi.</font> <br /> <font size=\"-1\"><strong>8 &quot;infine rimandato&quot;</strong>: &quot;alla fine dell\' anno fu rimandato a settembre&quot;.</font>','','1','1','0','7','2006-01-02 14:44:05','62','','2006-02-15 11:03:42','0','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','1','','','0','362','1-un-gatto');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '15','2. L\'africano','','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, II)</font></div>  &nbsp;&nbsp;&nbsp; Certamente dedicata ad un amico del Carlesso, Francesco Maria Cesari, &egrave; senz\' altro composta in epoca pi&ugrave; tarda e matura rispetto a &quot;Un gatto&quot;. Risale certamente alla terza liceo del Carlesso, poich&eacute; dice:&quot;Due anni or son conobbi&quot;, e lo conobbe in prima liceo. C\' &egrave; qualche critico che nega la pelle nera del Cesari, considerandola solo una caricatura, ma le fonti dell\' epoca sembrano attestare il contrario. Il significato dell\' ultimo verso dovrebbe invece essere ironico, data la profonda amicizia tra l\' africano e il poeta. Da notare la differenza tra l\' articolo usato per identificare gatto e africano. &quot;Un&quot; potrebbe essere stato usato per alludere a pi&ugrave; &quot;gatti&quot; (Buzzoni, Martini, ecc.), mentre l\' articolo determinativo davanti ad africano potrebbe alludere all\' originalit&agrave; e unicit&agrave; dell\' elemento: forse il Cesari viene dunque alluso come &quot;fenomeno da baraccone&quot;. <br /> Questo rimane uno dei pi&ugrave; importanti sonetti del Carlesso, ove si nota una certa erudizione ed un sapere ancora troppo enciclopedico (ovvero nozionistico), anche se &egrave; molto pi&ugrave; scorrevole del primo sonetto. <br /> &nbsp; <br /> &nbsp; <ul> &nbsp;&nbsp;&nbsp; Due anni or son conobbi <br />Un tale pelle-scura <br />Non lo facea per hobby <br />Eppur facea paura. <p>&nbsp;&nbsp;&nbsp; Un fatto invero strano:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <br />I genitori infatti <br />Di quel bell\'<sup><font size=\"-2\">1</font></sup> africano <br />Di latte paion fatti<sup><font size=\"-2\">2</font></sup>. </p><p>&nbsp;&nbsp;&nbsp; Il Ku Klux Klan lo vuole <br />Eppur difficil pare -&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 <br />Ch&eacute; tra le genti suole </p><p>&nbsp;&nbsp;&nbsp; mimetizzarsi e stare - <br />Poi prender quella mole<sup><font size=\"-2\">3</font></sup> <br />Sar&agrave; gran brutto affare<sup><font size=\"-2\">4</font></sup>. <br />&nbsp;</p> </ul>   <hr width=\"100%\" /> <br /> Metrica: Sonetto di settenari; rime: ABAB, CDCD, EFE, FEF. <br /> <font size=\"-1\"><strong>1 &quot;bell\'&quot;</strong>: Quel &quot;bell\'&quot; &egrave; chiaramente ironico.</font> <br /> <font size=\"-1\"><strong>2 &quot;di latte paion fatti&quot;</strong>: Diversamente dalla carnagione del figlio, la loro &egrave; bianca: quindi, normale.</font> <br /> <font size=\"-1\"><strong>3 &quot;mole&quot;</strong>: &egrave; controverso il significato di questa parola. Potrebbe alludere ad un peso esagerato, ma, secondo le fonti, l\' obesit&agrave; &egrave; una dei pochi difetti che il Cesari non aveva. Potrebbe dunque alludere alla somiglianza tra il Cesari e il Ghesini, compagno di classe di entrambi (fino alla quarta liceo) che dimostrava proprio questa malformazione. Altri critici pensano invece a &quot;mole&quot; come una parola &quot;rimediata&quot; dal poeta tra le poche che facessero rima con &quot;vuole&quot; e &quot;suole&quot;. Dato il cos&igrave; alto livello artistico delle sue opere, preferiamo comunque scartare codesta ipotesi.</font> <br /> <font size=\"-1\"><strong>4 &quot;sar&agrave; gran brutto affare&quot;</strong>: &quot;se anche il Ku Klux Klan lo trovasse, ci rimetterebbe solamente&quot;. Forse il Carlesso allude al fatto che il Cesari fosse un pessimo elemento.</font>','','1','1','0','7','2006-01-02 14:45:38','62','Riccardo Carlesso','2006-02-15 11:03:42','0','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','2','','','0','316','2-lafricano');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '16','3. Lo stagno','','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, III)</font></div>  &nbsp;&nbsp;&nbsp; Mai lo sperimentalismo del Carlesso ha raggiunto un culmine pi&ugrave; alto: la nuova forma metrica degli &quot;esametri sfortunati&quot; (vedi <em>metrica</em>, sotto), il linguaggio pi&ugrave; basso, un forte uso della ridondanza e di un ritmo frenetico, garantito dall\' uso dell\' esametro al posto del pi&ugrave; elegante settenario, l\' uso della poesia nella poesia (&quot;Puttana la rana&quot;, etc). <br /> &nbsp;&nbsp;&nbsp; La trama &egrave; semplice: una rana, un ranocchio e un girino stavano facendo il bagno in uno stagno. Improvvisamente arriva una famiglia che rompe l\' idillio: entra in acqua e disturba la famigliola. La rana decide cos&igrave; di spaventarli e questi, impauriti, scappano  via. <br /> &nbsp;&nbsp;&nbsp; E\' un\' opera altamente allegorica: pare infatti che la rana, il ranocchio e il girino fossero il simbolo di tre compagni di classe (due del liceo e uno delle medie) del Carlesso: la Chiara Bezzi (cos&igrave; chiamata perch&eacute; di liberi costumi), Matteo Atti (cosiddetto per le sue tendenze da &quot;altra sponda&quot;), e Marco Malagolini (per quest\' ultimo non &egrave; l\' aggettivo a determinare la persona - nonostante non fosse una cima - bens&igrave; l\' animale stesso: veniva detto girino perch&eacute; sembrava una larva, e il girino rende abbastanza l\' idea). <br /> &nbsp;&nbsp;&nbsp; Quest\' opera &egrave;, inoltre, visibilmente didascalica: il Carlesso tratta temi ormai dimenticati ma sempre attuali come il consumismo, il poco rispetto dell\' uomo verso la natura. Allegoricamente, d&agrave; anche un segno &quot;apocalittico&quot;: se l\' uomo non star&agrave; attento la natura gli si rivolter&agrave; contro (come la famigliola di rane ha fatto con la famigliola di umani). <br /> &nbsp;&nbsp;&nbsp; Duplice &egrave; dunque lo scopo del Carlesso: scherzoso nei confronti degli amici, ma anche critico nelle sue invettive contro l\' uomo. <br /> &nbsp; <br /> &nbsp; <br /> &nbsp; <ul> &nbsp;&nbsp;&nbsp; Vidi in uno stagno <br />Una famigliola <br />- Di rane l&igrave; a bagno - <br />Beata e tutta sola<sup><font size=\"-2\">1</font></sup>. <br />&nbsp; <p>&nbsp;&nbsp;&nbsp; Una famigliola&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <br />Di person (mi lagno) <br />Con la coca cola<sup><font size=\"-2\">2</font></sup> <br />Venne a far il bagno. </p><p>&nbsp;&nbsp;&nbsp; Disturb&ograve; la rana <br />E cos&igrave; il ranocchio:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 <br />Persino il girino. </p><p>&nbsp;&nbsp;&nbsp; &quot;Puttana la rana!&quot; <br />&quot;Finocchio il ranocchio!&quot; <br />&quot;Cretino il girino!&quot; </p><p>&nbsp;&nbsp;&nbsp; Urlaron in coro.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 15 <br />Usciron poi loro <br />Con poco decoro<sup><font size=\"-2\">3</font></sup>. <br />&nbsp;</p> </ul>   <hr width=\"100%\" /> <br /> Metrica: esametri sfortunati (cos&igrave; chiamati perch&eacute; in 17 versi; secondo le fonti dell\' epoca il 17 portava sfortuna); rime: ABAB, ABAB, CDE, CDE, FFF. <br /> <font size=\"-1\"><strong>1 &quot;Vidi... sola&quot;</strong>: Questi primi quattro versi vogliono costruire una situazione idilliaca e sottolineare la presa di posizione del poeta a favore degli animali contro l\' uomo (vedi introduzione).</font> <br /> <font size=\"-1\"><strong>2 &quot;con la coca cola&quot;</strong>: Non vuol tanto dire che andarono a fare il bagno con la lattina. Qui l\' autore vuole rappresentare con la coca cola il consumismo dell\' uomo (vedi introduzione).</font> <br /> <font size=\"-1\"><strong>3 &quot;con poco decoro&quot;</strong>: La famiglia usc&igrave; tanto spaventata che si sarebbe fatta compatire se ci fosse stato qualcun altro in giro.</font>','','1','1','0','7','2006-01-02 14:46:42','62','Riccardo Carlesso','2006-02-15 11:03:42','0','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','3','','','0','326','3-lo-stagno');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '17','4. Il buffone','','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, IV)</font></div>  &nbsp;&nbsp;&nbsp; Vengono presi nuovi temi in questo sonetto eccezionale per la sua dinamicit&agrave; e per i suoi concetti fusi con una tale sapienza narrativa da rasentare la cultura Palumbea (come ci dice l\'Autore in frammenti andati perduti). Innanzitutto il tema dell\' <em>amarezza</em>, come definita dall\'Autore stesso: la descrizione della classe e del suo amico sono tutt\' altro che positive. E\' facile capire a chi si riferisse l\'Autore nel sonetto in chiave ermetica, che segue quel tipo di canto carnascialesco - come precisa l\'autore - che non c\'entra niente ma fa capire il suo alto livello d\'erudizione. E\' l\'amico Matteo Atti. Molti indizi ci portano a lui: buffone, &quot;uom&quot;, l\'aria da baro, le due &quot;pale&quot; che fa venir su, la parvenza di coglione, contrapposta ai modi ben educati. <br /> &nbsp;&nbsp;&nbsp; Importante &egrave; l\' accenno a &quot;togate&quot; e &quot;palliate&quot;, che l\'autore sfrutta sapientemente per due motivi: per fare dell\'erudizione e per collegare il tema della commedia (ci&ograve; che, teoricamente, deve fare ridere) all\' Atti. <br /> &nbsp; <br /> &nbsp; <ul> &nbsp;&nbsp;&nbsp; Frequento classe tale <br />Che circo l\' appellaro<sup><font size=\"-2\">1</font></sup><br />Amico a me s&igrave; caro<sup><font size=\"-2\">2</font></sup><br />A simil rango sale<sup><font size=\"-2\">3</font></sup>. <p>&nbsp;&nbsp;&nbsp; Non che ce l\' abbia a male&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <br />Con tale &quot;uom&quot;<sup><font size=\"-2\">4</font></sup> ignaro <br />Ch\'ha l\'aria d\'un ver baro: <br />Venir fa su due pale<sup><font size=\"-2\">5</font></sup>. </p><p>&nbsp;&nbsp;&nbsp; A buon ragion buffone <br />Codesto fu appellato:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 <br />Sa dir solo togate<sup><font size=\"-2\">6</font></sup>. </p><p>&nbsp;&nbsp;&nbsp; Costui pare un coglione <br />Sebben ben educato. <br />S&igrave;: come ne\'e palliate<sup><font size=\"-2\">7</font></sup>. <br />&nbsp;</p> </ul>   <hr width=\"100%\" /> <br /> Metrica: Sonetto di settenari; rime: ABBA, CDDC, EFG, EFG. <br /> <font size=\"-1\"><strong>1 &quot;Che circo l\' appellaro&quot;</strong>: Con <em>amarezza</em>, il Carlesso parla delle condizioni non proprio di normalit&agrave; della propria classe.</font> <br /> <font size=\"-1\"><strong>2 &quot;Amico a me s&igrave; caro&quot;</strong>: Indica grande vicinanza affettiva tra il Carlesso e l\' Atti, probabilmente legata alla speranza di scroccare (cio&egrave; chiedere qualcosa ad un altro come per carit&agrave;) parte delle sue merende (consistenti in pinzoni e pizze). Il Carlesso &egrave; infatti tra i pochi Umanisti non disposti a prendere i voti minori per trarre una migliore sussistenza.</font> <br /> <font size=\"-1\"><strong>3 &quot;A simil rango sale&quot;</strong>: Assurge a rango di buffone, appunto.</font> <br /> <font size=\"-1\"><strong>4 &quot;uom&quot;</strong>: Il Carlesso, con molta amarezza, sottolinea la difficolt&agrave; nel distinguere il sesso dell\'Atti, per i suoi modi fuorvianti.</font> <br /> <font size=\"-1\"><strong>5 &quot;Venir fa su due pale&quot;</strong>: Contestatissima la traduzione di questo verso: secondo filologi e puristi, allora era in uso la frase poco fine: &quot;Far venir su due palle&quot;, ove le palle alludono alle gonadi maschili. La frase vuol dire: arrecare fastidio. Con grande sapienza retorica e narrativa l\'Autore avrebbe contratto &quot;palle&quot; in &quot;pale&quot; per fare rima con &quot;tale&quot;,&quot;sale&quot; e &quot;male&quot;.</font> <br /> <font size=\"-1\"><strong>6 &quot;Togate&quot;</strong>: Commedie di stampo romanizzante.</font> <br /> <font size=\"-1\"><strong>7 &quot;Palliate&quot;</strong>: Commedie di stampo grecizzante.</font>','','1','1','0','7','2006-01-02 14:47:19','62','','2006-02-15 11:03:42','0','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','4','','','0','349','4-il-buffone');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '18','5. Il lupo','','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, V)</font></div> &nbsp;&nbsp;&nbsp; Altro sonetto ermetico del Carlesso con una nuova sfumatura metrica che denota la sinossi formale nelle poesie che gli permette di adattare rime diverse a diverse esigenze. Il lupo in questione &egrave; chiaramente l\'amico Costa Jonata del quale l\'Autore vuole darci una migliore caratterizzazione. Nella prima quartina comincia col caratterizzare il lupo con melanconia e quasi compassione: &egrave; diverso dai colleghi, perch&eacute; timido: &egrave; perci&ograve; un emarginato dalla societ&agrave;; si passa da una piccola situazione ad una forte invettiva contro la societ&agrave; dell\'epoca. Nella seconda quartina si passa ad una durezza spietata che si ricollega al tema dell\'amarezza. Il tono torna spensierato nelle ultime due terzine. <br /> &nbsp; <br /> &nbsp; <ul> &nbsp;&nbsp;&nbsp; Conobbi un lupacchiotto <br />Diverso da colleghi: <br />Ei era un timidotto <br />Difficil che vi spieghi<sup><font size=\"-2\">1</font></sup>. <p>&nbsp;&nbsp;&nbsp; I\' penso sotto sotto&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <br />(Ma non che me ne freghi<sup><font size=\"-2\">2</font></sup>) <br />Che qui si fosse rotto<sup><font size=\"-2\">3</font></sup>, <br />Ma non che non lo neghi<sup><font size=\"-2\">4</font></sup>. </p><p>&nbsp;&nbsp;&nbsp; E\' invero s&igrave; peloso: <br />Un amuleto<sup><font size=\"-2\">5</font></sup>appare&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 <br />Nel petto s&igrave; villoso. </p><p>&nbsp;&nbsp;&nbsp; Non pu&ograve; andare al mare <br />Poich&eacute; il pi&ugrave; coraggioso <br />Non pu&ograve; far che scappare. <br />&nbsp;</p> </ul>   <hr width=\"100%\" /> <br /> Metrica: Sonetto di settenari; rime: ABAB, CDCD, EFE, FEF. <br /> <font size=\"-1\"><strong>1 &quot;Difficil che vi spieghi&quot;</strong>: E\' tanto timido che &egrave; difficile darne un\'idea. Questa &egrave; una <em>chiarissima</em> invettiva contro lo Stilnovo, che predicava l\' ineffabilit&agrave; della donna (cio&egrave; l\'incapacit&agrave; di descrivere le sue qualit&agrave;).</font> <br /> <font size=\"-1\"><strong>2 &quot;(Ma non che me ne freghi)&quot;</strong>: ma ci&ograve; non m\'interessa. Qui l\'Autore con abile sapienza narrativa cerca di distaccarsi e di prendere le distanze dall\'oggetto del discorso, in modo da dirlo in maniera pi&ugrave; oggettiva.</font> <br /> <font size=\"-1\"><strong>3 &quot;Che qui si fosse rotto&quot;:</strong> Che si fosse stancato di stare a scuola.</font> <br /> <font size=\"-1\"><strong>4 &quot;Ma non che non lo neghi:</strong> Ma, nonostante sia un pensiero, lo nego. Difficilissimo verso di cui solo il Carlesso poteva avere un\'idea, date le sue eccezionali doti matematiche, umanistiche, musicali, intellettive, sinottiche, misogine, introspettive, ineffabili, quantistiche, annichilistiche e dialettiche (come ci perviene da un autocommento dell\'Autore contenuto in uno dei suoi tanti frammenti).</font> <br /> <font size=\"-1\"><strong>5 &quot;amuleto&quot;</strong>: nel petto il Costa pare avere un insieme di peli della forma simile ad un amuleto.</font>','','1','1','0','7','2006-01-02 14:47:50','62','','2006-02-15 11:03:42','62','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','5','','','0','351','5-il-lupo');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '19','6. La mongolfiera','','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, VI)</font></div>  &nbsp;&nbsp;&nbsp; E\' con questo capolavoro che l\'Autore comincia a fare suoi gl\'insegnamenti dei grandi maestri del passato e a tentare un eccezionale e ben riuscito sperimentalismo: l\'indovinello carlessiano. Si tratta di un numero variabile di quintine (generalmente tre) ove la rima &egrave; di tipo ABBAX, dove A e B diventano cambiano, mentre la X rimane costante: essa fa rima con il nome da indovinare. <br /> &nbsp;&nbsp;&nbsp; E\' semplice intuire la soluzione: trattasi di Gheso, soprannome di Alessandro Ghesini, di cui gi&agrave; si &egrave; parlato ne &quot;L\' africano&quot;. Il paragone con la mongolfiera, la gabbia pi&ugrave; molle della gomma (la pancia), il fatto che praticasse nuoto (che ci &egrave; confermato dalle fonti dell\' epoca) non lasciano dubbi. <br /> &nbsp; <br /> &nbsp; <ul> &nbsp;&nbsp;&nbsp; Colpimmi un fatto strano, <br />Che vidi ieri sera: <br />Fu una mongolfiera. <br />Parea un eroplano, <br />Ma da zavorra leso.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <p>&nbsp;&nbsp;&nbsp; Parvenza proprio umana <br />Non puossi dir ch\'ei abbia <br />Par chiuso in una gabbia <br />S&igrave; molle ch\'ea par lana. <br />Notevol\'&egrave;\'l suo peso.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 </p><p>&nbsp;&nbsp;&nbsp; La testa riccioluta, <br />Il viso arrotondato, <br />Nuotar ei ha provato: <br />Sicch&eacute; in balena muta<sup><font size=\"-2\">1</font></sup> <br />Cap\'rete<sup><font size=\"-2\">2</font></sup> com\'&egrave; obeso.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 15 <br />&nbsp;</p> </ul>   <hr width=\"100%\" /> <br /> Metrica: Indovinello Carlessiano. Forma: ABBAX,CDDCX,EFFEX, dove X fa rima col nome da indovinare. <br /> <font size=\"-1\"><strong>1 &quot;Sicch&eacute; in balena muta&quot;</strong>: Quando lo fa sembra una balena o, pi&ugrave; letteralmente, si tramuta in essa.</font> <br /> <font size=\"-1\"><strong>2 &quot;Cap\'rete&quot;</strong>: forma contratta di &quot;capirete&quot;.</font>','','1','1','0','7','2006-01-02 14:48:39','62','','2006-02-15 11:03:42','0','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','1','0','6','','','0','389','6-la-mongolfiera');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '20','7. La suocera','','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, VII)</font></div> &nbsp;&nbsp;&nbsp; Il sarcasmo del Carlesso sfiora vette massime in questo capolavoro formale e concettuale, ove erudizione e satira sono fuse sapientemente come solo l\'Autore sa fare. Non v\'&egrave; pi&ugrave; la minima traccia di nozionismo, indice della maturazione dell\'arte e del genio carlessiano. E\' facile capire la soluzione dell\'indovinello carlessiano, ma non possiamo pubblicarlo per motivi di censura: se questo arrivasse ad orecchie sbagliate il Carlesso teme che peggiorerebbe ulteriormente il suo rapporto con questa &quot;proff.essa&quot;, gi&agrave; non troppo buono&quot;, come leggiamo in un frammento dell\'epoca. <br /> &nbsp; <br /> &nbsp; <ul> Conobbi una proff.essa<sup><font size=\"-2\">1</font></sup><br />Ch\'avemmo dal quart\'anno <br />E fino a quest\'altr\'anno <br />Opprimeracci essa. <br />Non balla questa qua<sup><font size=\"-2\">2</font></sup>.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <p>Per nulla ea s\'indigna <br />E parla e urla e parla<sup><font size=\"-2\">3</font></sup> <br />Difficil &egrave; calmarla: <br />La testa par \'na pigna<sup><font size=\"-2\">4</font></sup>. <br />Vad\'al paese l&agrave;<sup><font size=\"-2\">5</font></sup>.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 </p><p>Conciossi i suoi capelli; <br />\'bbligocci ad osservalli<sup><font size=\"-2\">6</font></sup>; <br />(parlando di metalli)<sup><font size=\"-2\">7</font></sup> <br />Fececci dir: &quot;Son belli!<sup><font size=\"-2\">8</font></sup>&quot;. <br />Capisci chi &egrave; \'sta qua?&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 15 <br />&nbsp;</p> </ul>    <hr width=\"100%\" /> <br /> Metrica: Indovinello Carlessiano. Forma: ABBAX,CDDCX,EFFEX, dove X fa rima col nome da indovinare. <br /> <font size=\"-1\"><strong>1 &quot;proff.essa&quot;</strong>: Forma contratta di professoressa, in cui &egrave; stata raddoppiata la F per enfatizzare il neologismo. Esso &egrave; particolarmente astuto in quanto avvicina la parola a &quot;prof fessa&quot;, che &egrave; suo omofono (<em>fesso</em>, nello slang degli anni 80-90, voleva dire stupido).</font> <br /> <font size=\"-1\"><strong>2 &quot;Non balla questa qua&quot;</strong>: Allude ad una star del momento che ballava all\'epoca e di cui la persona da indovinare dev\'essere omonima.</font> <br /> <font size=\"-1\"><strong>3 &quot;E parla e urla e parla&quot;</strong>: E con estrema facilit&agrave; passa da un umore all\'altro.</font> <br /> <font size=\"-1\"><strong>4 &quot;La testa par \'na pigna&quot;</strong>: La testa &egrave; dura come una pigna, o forse &egrave; vuota come una pigna.</font> <br /> <font size=\"-1\"><strong>5 &quot;Vad\'al paese l&agrave;&quot;</strong>: Modo elegantemente poetico per dire &quot;Che vada a quel paese&quot;, ovvero: &quot;Stia zitta&quot;.</font> <br /> <font size=\"-1\"><strong>6 &quot;\'bbligocci ad osservalli&quot;</strong>: &quot;Ci obblig&ograve; ad osservarli&quot;.</font> <br /> <font size=\"-1\"><strong>7 &quot;Parlando di metalli&quot;</strong>: Allude probabilmente alla professione della &quot;proff.essa&quot;.</font> <br /> <font size=\"-1\"><strong>8 &quot;Fececci dir: Son Belli!&quot;</strong>: Ci fece dire: &quot;Sono belli!&quot; Questo &egrave; il verso pi&ugrave; rispecchiante l\' amarezza carlessiana. Con esso il Carlesso sottolinea come spesso i professori <em>usino</em> gli studenti per scopi puramente egoistici, e come li spingano a non avere idee loro proprie, bens&igrave; le impongono (&quot;Fececci dir: Son belli!). Quest\'ipocrisia &egrave; sapientemente enfatizzata dal punto esclamativo.</font><br /> ','','1','1','0','7','2006-01-02 14:49:04','62','Riccardo Carlesso','2006-02-15 11:03:42','62','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','7','','','0','449','7-la-suocera');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '22','Il motore a erezione','','Sia dato un treno a bassissimo attrito, possibilmente giapponese su rotaia<br /> magnetica.<br /> Sia dato un vagone in cui le sedie sono girevoli.<br /> Si prendano n negroni N1,. . . ,Nn (non la bevanda, gli uomini) e si mettano<br /> seduti con la sedia orientata verso il RETRO del treno.<br /> Si prenda una bionda B<sub>1</sub>, gnocca e sexy per ipotesi (donde l\'iniziale greca),<br /> che giace in fondo al vagone VESTITA.<br /><strong><br /></strong>','<strong>Fase 1</strong><br /> La bionda si spoglia e triggera dunque una reazione negli <em>n</em> neri.<br /> Si pu&ograve; stimare lo spostamento di baricentro di un uomo di circa 80 Kg (m<sub>u</sub>)<br /> di un membro di massa 40 grammi (m<sub>m</sub>) e lunghezza 20 (l<sub>p</sub>) cm secondo una<br /> formula semplificata (vale x un rapporto bassissimo tra mm e mu):<br /> <br /> X<sub>g</sub> = l<sub>p </sub>&middot; 2 &middot; m<sub>m</sub>/m<sub>u</sub> = 50&mu;m<br /> <br /> Ora, lo \'sforzo\' congiunto di N<sub>1</sub>,. . . ,N<sub>n</sub> pu&ograve; far avanza il vagone in AVANTI<br /> di, diciamo, un centimetro (= n &middot;  X<sub>g</sub>). Il problema &egrave; quando l\'erezione INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '21','8. 101010','','<div align=\"right\"><font size=\"-1\">(da Il Canzoniere di Riccardo Carlesso, VIII)</font></div> &nbsp;&nbsp;&nbsp; Quanto a sperimentalismo, dovremmo forse annoverare questa lirica tra le pi&ugrave; riuscite dell\'Autore, che ha osato spingersi in l&agrave; ben sapendo i rischi che correva; la fortuna di quest\'opera stup&igrave; invece l\'autore stesso... che per la prima volta spinge i temi ad una dimensione cosmica. Cos&igrave; come a Dante fu caro il 3 e con esso il 9, l\'autore predilige un altro numero che pare essere risposta ad ogni domanda sulla &quot;Vita, l\'Universo e Tutto&quot;, come ci ricorda l\'Autore da un frammento dell\'epoca. Viste le grandi doti matematiche del Carlesso, era da aspettarsi una poesia che unisse - come da pi&ugrave; grande sogno dell\'Autore - umanesismo e scienza. Sebbene \'<em>Meno bi fratto due a</em>\' fosse sicuramente un gran poema che veniva insegnato a memoria ai bambini fin dalle medie, vista la portata artistica (ma anche l\'utilit&agrave;) della poesia, <em>101010</em> si spinge oltre, toccando vette inaudite. Le parole si fanno quasi  musicali, in un simbolismo che assurge a Boole stesso. \'<em>Completa ma essenziale, non potrebbe esistere poema al mondo che con meno mattoni potesse costruire un s&igrave; mirabile castello formale</em>\', questo un commento lasciatoci dal grande DeSanctis. Questa poesia miscela sapientemente nuove vie (che Carlesso ha spesso aperto da bravo pioniere) e vecchie certezze: si va dall\'invenzione del ternario bittico all\'uso della ridondanza e dell\'ironia, da citazioni falliche all\'uso di un simbolismo cosmico... <br />  &nbsp; <ul> &nbsp;&nbsp;&nbsp; 1\'01110 11010011 <sup><font size=\"-2\">1</font></sup><br />101110 101010 110010 <br />101110 101010 110010<sup><font size=\"-2\">2</font></sup><br />101 110 1011 111 1011 <p>&nbsp;&nbsp;&nbsp; 10 10011 10 11001001!&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 <br />100 10 110110011 1010 <br />101 10010010 000000<sup><font size=\"-2\">3</font></sup> <br />101001 10101 11010 1. </p><p>&nbsp;&nbsp;&nbsp; 100 10101 10101 1101 10<sup><font size=\"-2\">4</font></sup>. <br />1111111111111111111<sup><font size=\"-2\">5</font></sup>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10 <br />110 101! 1010 101 101 10; </p><p>&nbsp;&nbsp;&nbsp; 110101 110 100010 101 <br />10110 110110 10011 10 <br />1001 101010 110101 10 x3!<sup><font size=\"-2\">8</font></sup>. <br />&nbsp; <br />&nbsp;</p> </ul>   <hr width=\"100%\" /> <br /> Metrica: Sonetto di \'ternari bittici\'; rime: ABBA, ABBA, BAB, ABC,. <br /> <font size=\"-1\"><strong>1 &quot;11010011 &quot;</strong>: Gi&agrave; dal primo verso scorgiamo l\'incalzante critica dell\'Autore verso le destre, la TV, la Microsoft (ricordiamo che alla fine del \'900 vi era la libert&agrave; di pensiero e di stampa sul Grande Colosso), l\'aumento dell\'entropia e la fine del mondo, proponendo velatamente un nesso tra i fenomeni .</font> <br /> <font size=\"-1\"><strong>2 &quot;101110 101010 110010&quot;</strong>: Da notare la ripetizione del verso, certo posta ad enfatizzare il senso delle parole.</font> <br /> <font size=\"-1\"><strong>3 &quot;000000&quot;</strong>: E\' chiaramente allusivo ad un amico di Universit&agrave;, il Biondini.</font> <br /> <font size=\"-1\"><strong>4 &quot;10&quot;</strong>: In questo contesto, &egrave; chiaramente ironico, sebbene l\'Autore difficlmente scherzi su questi temi.</font> <br /> <font size=\"-1\"><strong>5 &quot;1111111111111111111&quot;</strong>: pare infatti che il Biondini fosse particolarmente \'dotato\'.</font> <br /> <font size=\"-1\"><strong>6 &quot;... x3!&quot;</strong>: pi&ugrave; che controversa l\'interpretazione di quest\'ultimo verso... molti studiosi accrediterebbero quest\'ultimo verso al simbolismo dell\'Autore che mai come in questo poema era stato toccato; matematici e filologi una volta tanto insieme porrebbero questo \'x3\' come chiave della soluzione del problema cosmico: chi siamo? da dove veniamo? Il problema sussiste: cosa va moltiplicato per il numero cos&igrave; caro a Dante, uno dei pochi poeti italiani ad essere ancora all\'altezza col grande Carlesso (nonostante la scoperta del <em>vero </em>motivo del rifiuto di Beatrice)? Certamente un numero insito nella metrica: chi sospetterebbe il numero di sillabe, chi un anagramma dell\'et&agrave; a cui &egrave; morto Lyapunov (per la prima volta <em>davvero </em>stabilmente), ma la domanda &egrave; ancora aperta.</font>','','1','1','0','7','2006-01-02 14:49:42','62','','2006-02-15 11:03:42','62','0','0000-00-00 00:00:00','2006-01-02 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','8','','','0','411','8-101010');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '25','Scoperte inutili','','<p>1. Se tu urlassi per 8 anni, 7 mesi e 6 giorni, produrresti abbastanza energia sonora per riscaldare una tazza di caff&egrave;. [Non mi sembra che ne valga la pena]<br />  2. Se tu producessi costantemente flatulenze per 6 anni e 9 mesi, il gas risultante fornirebbe energia equivalente a quella di una bomba atomica. [Questo potrebbe avere pi&ugrave; senso] <br />  3. L\'orgasmo di un maiale dura 30 minuti. [Nella mia prossima vita voglio essere un maiale... Ma come hanno fatto a scoprirlo? E PERCH&Eacute;?]<br />  4. Per sbattere la tua testa contro un muro sono necessarie 150 calorie all\'ora. [Non riesco a lasciar perdere quella cosa dei maiali...] </p>','<p>1. Se tu urlassi per 8 anni, 7 mesi e 6 giorni, produrresti abbastanza energia sonora per riscaldare una tazza di caff&egrave;. [Non mi sembra che ne valga la pena]<br /> <br />  2. Se tu producessi costantemente flatulenze per 6 anni e 9 mesi, il gas risultante fornirebbe energia equivalente a quella di una bomba atomica. [Questo potrebbe avere pi&ugrave; senso] <br /> <br />  3. L\'orgasmo di un maiale dura 30 minuti. [Nella mia prossima vita voglio essere un maiale... Ma come hanno fatto a scoprirlo? E PERCH&Eacute;?]<br /> <br />  4. Per sbattere la tua testa contro un muro sono necessarie 150 calorie all\'ora. [Non riesco a lasciar perdere quella cosa dei maiali...] <br /> <br />  5. Umani e delfini sono le uniche specie che fanno sesso per piacere. [&Egrave; per questo che Flipper sorride sempre? E i maiali hanno orgasmi di mezz\'ora? Non mi sembra giusto] <br /> <br />  6. Chi usa la mano destra vive, in media, nove anni pi&ugrave; a lungo dei mancini. [Se sei ambidestro che fai, una media?]<br /> <br />  7. Una formica pu&ograve; sollevare cinquanta volte il suo peso, pu&ograve; trascinare trenta volte il suo peso e cade sempre alla sua destra se intossicata. [Ma i contribuenti pagano le tasse per sovvenzionare questo tipo di ricerche?] <br /> <br />  8. Gli orsi polari sono mancini. [E chi lo sa? E come lo hanno capito? E soprattutto chi se ne frega? Vuol dire che vivono nove anni in meno? E i maiali hanno un orgasmo di mezz\'ora?] <br /> <br />  9. Un pesce gatto ha pi&ugrave; di 27.000 papille gustative. [Ma cosa ci sar&agrave; di cos&igrave; saporito sul fondo di uno stagno?] <br /> <br />  10. Una pulce pu&ograve; saltare una distanza pari a 350 volte la lunghezza del suo corpo. &Egrave; come se un uomo potesse saltare da un capo all\'altro di un campo di calcio. [Mezz\'ora... ma ti rendi conto?? E perch&eacute; proprio i maiali???] <br /> <br />  11. Uno scarafaggio decapitato sopravvive nove giorni prima di morire. [Che schifo]<br /> <br />  12. Il maschio della mantide religiosa non pu&ograve; accoppiarsi finch&eacute; la sua testa &egrave; attaccata al corpo. La femmina d&agrave; inizio all\'accoppiamento decapitando il maschio. [&quot;Tesoro, sono a casa, metti la testa sul ceppo che stasera ho voglia...&quot;] <br /> <br />  13. Alcuni leoni si accoppiano pi&ugrave; di cinquanta volte al giorno. [Nella mia prossima vita voglio comunque essere un maiale... Qualit&agrave;, non quantit&agrave;] <br /> <br />  14. Le farfalle sentono i sapori con i piedi. [No comment]<br /> <br />  15. L\'occhio dello struzzo &egrave; pi&ugrave; grande del suo cervello. [Conosco persone cos&igrave;] <br /> <br />  16. Le stelle marine non hanno cervello. [Conosco anche persone cos&igrave;. E quindi, in conclusione... maledetti maiali!] <br /> <br />  17. &Egrave; impossibile leccarsi il gomito. <br /> <br />  18. Un coccodrillo non pu&ograve; tirare fuori la lingua. <br /> <br />  19. Il cuore di un gamberetto &egrave; nella testa. <br /> <br />  20. In uno studio effettuato su circa 200.000 struzzi, durato pi&ugrave; di 80 anni, non si &egrave; mai visto uno struzzo mettere la testa nella sabbia.<br /> <br />  21. I porci non possono fisicamente guardare il cielo. [Per&ograve; hanno un orgasmo di mezz\'ora...] <br /> <br />  22. Pi&ugrave; del 50% delle persone, in tutto il mondo, non hanno mai fatto o ricevuto telefonate. <br /> <br />  23. I ratti e i cavalli non possono vomitare. <br /> <br />  24. Se starnutite troppo forte, potreste rompervi una costola. <br /> <br />  25. Se tentate di trattenere uno starnuto, potrete causarvi la rottura di una vena nel cervello o nella nuca, e potreste morire. [\'azz, tocca ferro...] <br /> <br />  26. Se, contro natura, tenete gli occhi aperti mentre startunite, potrebbero uscirvi dalle orbite. [Ma accidenti!] <br /> <br />  27. I ratti si moltiplicano cos&igrave; rapidamente che, in soli 18 mesi, una coppia pu&ograve; avere pi&ugrave; di un milione di discendenti. <br /> <br />  28. L\'accendino &egrave; stato inventato prima dei fiammiferi.<br /> <br />  29. Il 35% delle persone che usano annunci personali nei giornali per trovare un compagno o compagna sono sposati. <br /> <br />  30. Nel mondo, il 23% dei problemi alle macchine fotocopiatrici sono causate da persone che si siedono sull\'apparecchio per fotocopiarsi il sedere. <br /> <br />  31. Nella durata media di una vita, una persona che dorme ingoier&agrave; 10 ragni e 70 insetti. <br /> <br />  32. La maggior parte dei rossetti contiene delle lische di pesce. <br /> <br />  33. Come le impronte digitali, l\'impronta della lingua &egrave; diversa per ognuno. <br /> <br />  34. Il 90% delle persone che leggono queste notizie prova a leccarsi il gomito. E tu di certo avrai prov                                veduto a mantenere alta questa percentuale!</p>  ','1','3','0','18','2006-01-09 10:40:20','62','','2006-02-15 11:03:42','62','0','0000-00-00 00:00:00','2006-01-09 01:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','34','','','0','299','scoperte-inutili');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '26','Links di Carlesso','Links di Carlesso','  <p>&nbsp;I miei link preferiti, usati per cose varie (lavoro, cose personali), ...<br /> </p>   <ul> <li>HTML&nbsp;</li> <li>     <blockquote> <ul> <li>CSS:<span class=\"moz-txt-citetags\">&nbsp; </span><a href=\"http://www.phoenity.com/newtedge/\">http://www.phoenity.com/newtedge/</a> (Bergonz) -&nbsp; <a href=\"http://www.ylab.nl/ref/css/\">Ylab | CSS Quick reference | Text</a> - (Fero) <a href=\"http://www.w3schools.com/\">http://www.w3schools.com/</a>.</li>  </ul></blockquote>   </li>   <li>     <blockquote><br />     </blockquote>     <br />   </li> </ul> <ul>   <li>PERSONALI&nbsp;</li> </ul> <ul>   <li>     <blockquote>&nbsp;MAGIC:        <a href=\"http://www.magic-league.com/deck/13041/goth_slaver.html#Goth%20Slaver189\">Goth Slaver Deck - Vintage (T1) Deck lists</a> (liste mazzi)        <a href=\"http://www.magic-league.com/decks/\">Magic: the Gathering Decks</a> (boh) www (DB carte)<br />     </blockquote>   </li>   <li>     <blockquote>MIEI SITI:        <a href=\"http://master.goliardia.org/mysql/\">master.goliardia.org &gt;&gt; localhost | phpMyAdmin 2.6.0-pl2</a> <br />     </blockquote>   </li> </ul> ','','-1','2','0','17','2006-01-09 13:01:55','62','','2006-09-14 11:57:46','64','0','0000-00-00 00:00:00','2006-01-09 01:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','1','','','0','373','links-di-carlesso');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '27','Un processo pu conoscere il pap?!?','','  <pre>ls -l /proc/self/fd/0</pre>  (in perl c\'e\' anche lstat, in man perlfunc).<br /> Continua alla prox pagina!!!!\r\n<pre>\r\n # cat NOMEFILE&nbsp; | scriptmioinperl\r\n # ls -l /proc/self/fd/0      \r\n</pre> \r\n \r\nIn effetti sono stato frettoloso, anche se la sostanza e\' quella: avevo  pensato a script &lt; nomefile, tu avevi scritto cat per dire zcat, immagino. <br />  Nel caso zcat tocca andare a leggere il quarto campo di /proc/self/stat,  che e\' il ppid, e da li\' lstat(<em class=\"moz-txt-slash\"><span class=\"moz-txt-tag\">/</span>proc<span class=\"moz-txt-tag\">/</span></em>$ppid/fd/0). In perl e\' facile, in  shell occhio che $() e\' un altro processo: export io=$$; ls -l  <em class=\"moz-txt-slash\"><span class=\"moz-txt-tag\">/</span>proc<span class=\"moz-txt-tag\">/</span></em>$(cat <em class=\"moz-txt-slash\"><span class=\"moz-txt-tag\">/</span>proc<span class=\"moz-txt-tag\">/</span></em>$io/stat|cut \'-d \' -f4)/fd/0 <br />  La morale e\' che in proc c\'e\' tutto, pero\' e\' anche vero che non tutto  si riesce a fare in pipe: passa i nomi dei file.    </p>','','1','3','0','23','2006-01-09 13:20:45','62','','2006-02-15 11:03:42','64','0','0000-00-00 00:00:00','2006-01-09 01:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','5','0','13','','','0','278','un-processo-punoscere-il-pap');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '29','Catene di Sant\'Antonio: come far smettere i vostri amici','','<p><em>Come fare quando una persona che stimate vi manda ogni genere di cagata possibile?!? Fate copia e incolla di questo file e speditela a chiunque vi mandi questa catena. Credo sia corretto mandarla SOLO per difesa e mai a tutti i vostri amici, se no cadete nel circolo vizioso.</em><br />--<br />Ciao, il mio nome &egrave;&uml; Arild Ovesen, soffro di  malattie rare e mortali, cattivi  risultati scolastici, estrema  verginit&agrave; , paura di venire rapito e ucciso  mediante elettroshock anale  e senso  di colpa per non aver inoltrato 50 miliardi  di catene di  Sant\'Antonio, mandatemi da persone chepensano davvero che se uno le   inoltra la povera piccola bambina di 6 anni a Foligno con un capezzolo  in fronte  riuscir&agrave;  a procurarsi abbastanza denaro per toglierlo prima  che i genitori la  vendano al Circo Orfei.    </p>','Ciao, il mio nome &egrave;&uml; Arild Ovesen, soffro di  malattie rare e mortali, cattivi  risultati scolastici, estrema  verginit&agrave; , paura di venire rapito e ucciso  mediante elettroshock anale  e senso  di colpa per non aver inoltrato 50 miliardi  di catene di  Sant\'Antonio, mandatemi da persone chepensano davvero che se uno le   inoltra la povera piccola bambina di 6 anni a Foligno con un capezzolo  in fronte  riuscir&agrave;  a procurarsi abbastanza denaro per toglierlo prima  che i genitori la  vendano al Circo Orfei.    Prima di tutto devi mandare  questa lettera a 7491 persone entro i prossimi 5  secondi, altrimenti  verrai stuprato/a da un montone impazzito e gettato fuori da  un  edificio altissimo per  cadere in una collina di escrementi animali.  Se non  lo farai, a causa di uno strano virus la ventola dentro il pc si metter&agrave;  a  girare al contrario e vi risucchier&agrave;  il processore. Dopo una  serie di lampi di  colore blu, dal vostro lettore cd (se avete il masterizzatore &egrave;&uml; peggio), uscir&agrave;   il totem (o tantra) della buona sorte  che ha gi&agrave;  fatto il giro del mondo tre   volte (e mi ha confidato di  essersi rotto il cazzo perch&egrave;&uml; vorrebbe starsene  cinque minuti a casa),  vi appogger&agrave;  alla pecorina sulla tastiera e vi far&agrave;  il  carretto,  indipendentemente se siete donne o uomini.    Per ogni messaggio che  manderete all\'indirizzo blablabla@savut.it  un\'associazione doner&agrave;  un  quarto di dollaro per comprare un aereo all\'aviazione  americana che  servir&agrave;  per tirare gi&agrave;&sup1;  un\'altra funivia in Italia. E per ogni  mail che  manderete alla Microsoft o che mander&agrave;  un\'altra persona dopo avere   ricevuto l\'informazione da voi vi verr&agrave;  portata una pizza quattro stagioni a  casa vostra da Bill  Gates in persona.    Io all\'inizio non ci  credevo poi ne ho spedite tante e dopo tre settimane mi &egrave;&uml;  arrivato  addirittura un calzone farcito !!!  &egrave;  tutto vero!!! Perch&egrave;&uml; questa   lettera non &egrave;&uml; come tutte quelle false, QUESTA &egrave;&uml; del tutto autentica!!!  Del  tutto vera.     Ecco il programma:  Mandala a 1 persona: 1 persona si  incazzer&agrave;  perch&egrave;&uml; gli hai  mandato una stupida catena di Sant\'Antonio.   Mandala a 2-5 persone: 2-5 persone si incazzeranno perch&egrave;&uml; gli hai  mandato una  stupida catena di Sant\'Antonio.   Mandala a 5-10 persone: 5- 10 persone si incazzeranno perch&egrave;&uml; gli hai mandato una  stupida catena di  Sant\'Antonio, e forse valuteranno l\'opportunit&agrave;  di  sopprimerti.    Mandala a 10-20 persone: 10-20 persone si incazzeranno perch&egrave;&uml; gli hai mandato  una stupida catena di Sant\'Antonio, e faranno saltare te, la  tua casa, la tua  famiglia e il tuo gatto in  aria.     Erode Scannabelve,  un pediatra mannaro di Trieste, non sped&igrave; a nessuno questa  mail: dei  suoi tre figli uno cominci&ograve; a drogarsi, il secondo si iscrisse a   Ingegneria dei Materiali e il  terzo entr&ograve; in Forza Italia .    Turiddu  Von Wasselvitz, un allenatore di farfalle da combattimento austro-  siculo, si fece beffe di questa mail ad alta voce, e in quello stesso istante gli  esplose la testa.    Meo Smazza, pornodivo shakespeariano,  non diede alcun peso a questa mail: ignoti  gli riempirono un  profilattico di azoto liquido, e lui se ne accorse solo dopo  averlo  indossato.    Un tizio che conosco non ha diffuso questa mail e ha  disimparato a andare in  bicicletta.  Un altro sfigato che conosco se ne  &egrave;&uml; sbattuto di questa mail, ma  gi&agrave; non sapeva andare in bicicletta. E\'  per&ograve; stato investito in motorino da un  camionista inglese ubriaco che, ovviamente, guidava contromano.  Mio cugino  invece non ha divulgato  questo messaggio e finalmente ha perso la sua verginit&agrave;  ... anale.     Se  inviate questo messaggio a tutti coloro che conoscete, perderete 2  minuti ma  forse finiranno di rompere con queste cazzo di catene di  Sant\'Antonio.  Se non  lo farete vi cadr&agrave;  addosso un pianoforte.     ','1','3','0','18','2006-01-20 17:59:01','64','','2006-02-15 11:03:42','62','0','0000-00-00 00:00:00','2006-01-20 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','3','0','33','','','0','408','catene-di-santantonio-come-far-smettere-i-vostri-amici');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '30','barze bis','','<strong>1) </strong> L&#39;&#39;ingegnere meccanico e l&#39;&#39;ingegnere civile.<br /><br />  <strong>2)</strong> Dio dev&#39;essere per forza un ingegnere. Solo un ingegnere, creando l&#39;uomo, poteva mettere un parco dei divertimenti vicino allo sbocco delle fogne','','0','3','0','1','2006-01-23 20:23:21','64','','2006-10-22 00:47:21','64','0','0000-00-00 00:00:00','2006-01-23 00:00:00','0000-00-00 00:00:00','','','','3','0','8','','','0','62','barze-bis');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '31','Centrino: come cambiare la frequenza di CPU dinamicamente (linux)','','  <p>Ho chiesto al mio collega <strong>Luca Ferroni</strong> un aiuto su come modificare la frequenza di cpu del mio portatile (lenta in modalit&agrave; &#39;film in treno&#39;, veloce in modalit&agrave; batteria in carica). Ecco cosa mi risponde:<br />     </p> <pre>echo &quot;performance&quot; &gt;&gt; /sys/devices/system/cpu/cpu0/cpufreq/scaling_governor  </pre>','   <p><em>Il giorno mar, 17/01/2006 alle 10.40 +0100, Riccardo Carlesso ha scritto: </em><br />    vorrei gli scriptx e della documentazione pliz  <br /> </p><pre>#!/bin/bash<br />echo &quot;performance&quot; &gt;&gt; /sys/devices/system/cpu/cpu0/cpufreq/scaling_governor   </pre>     <p>     questo &egrave; lo script per mettere il processore al max della frequenza.  gli altri sono &quot;powersave&quot; e &quot;userspace&quot; powersave per risparmiare energia (quindi lento) e userspace in cui da utente puoi settare la velocita.  le velocita disponibili le vedi in  <br />    </p>   <pre>/sys/devices/system/cpu/cpu0/cpufreq/scaling_available_frequencies  </pre>  la velocita corrente la puoi settare in : <br />   <pre>/sys/devices/system/cpu/cpu0/cpufreq/scaling_cur_freq  </pre>    per la documentazione io vedrei la documentazione acclusa nei sorgenti del kernel (devi cercare governor) cmq l&#39;opzione nella config del kernel &egrave; fra le opzioni per i processori (nello stesso menu della scelta del processore)  in gnome lo visualizzi aggiungendo al pannello l&#39;applet <u>system&amp;hardware ----&gt; variazione della velocita&#39; del processore  </u>cmq in kde c&#39;e&#39; sicuramente qualcosa di analogo se non migliore. <br />    ciao <br />     --<br />     Luca Ferroni <br /><br />  <p>&nbsp;</p>   ','1','3','0','23','2006-01-24 11:43:07','64','Luca Ferroni','2006-09-14 11:53:43','64','0','0000-00-00 00:00:00','2006-01-24 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','16','0','12','','','0','415','centrino-come-cambiare-la-frequenza-di-cpu-dinamicamente-linux');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '32','Statistiche su donne e sesso','','<strong> STATISTICHE</strong><br /> - il 10% delle donne ha fatto l\'amore nell\'ora che segue il primo incontro;<br /> - il 20% degli uomini ha fatto l\'amore in luoghi insoliti;<br /> - il 36% delle donne &egrave; piuttosto favorevole al naturismo;<br /> - il 45% delle donne preferisce gli uomini bruni con gli occhi blu;<br /> - il 46% delle donne pratica la sodomia;<br /> - il 70% delle donne preferisce fare l\'amore la mattina;<br /> - l&rsquo;80% degli uomini non ha mai avuto relazioni omosessuali;<br /> - il 90% delle donne amerebbe fare l\'amore nella foresta;<br /> - il 99% delle donne non ha mai fatto l\'amore in ufficio.<br /> <strong><br /> CONCLUSIONI</strong><br /> Statisticamente &egrave; pi&ugrave; probabile che voi sodomizziate una sconosciuta in una <br /> foresta di prima mattina che rispetto a fare l\'amore in ufficio a fine serata.<br /> <strong><br /> MORALE</strong><br /> NON RESTATE IN UFFICIO FINO A TARDI: NON SERVE A UN CAZZO!!!','','1','3','0','18','2006-01-25 16:23:29','64','','2006-02-15 11:03:42','62','0','0000-00-00 00:00:00','2006-01-25 00:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','32','','','0','352','statistiche-su-donne-e-sesso');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '33','Foto (prototipo)','Foto','<p>Qui forse vedrete le foto.</p><p>forse ma forse.</p><p>vedremo.</p><p>vedrete :)</p>','','1','0','0','0','2006-01-26 11:13:55','62','','0000-00-00 00:00:00','0','0','0000-00-00 00:00:00','2006-01-26 00:00:00','0000-00-00 00:00:00','','','menu_image=-1\nitem_title=1\npageclass_sfx=\nback_button=\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=0\nprint=0\nemail=0','1','0','2','','','0','290','foto');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '34','Matematici e Ingegneri...','','<p><em><img src=\"http://www.palladius.it/joomlait//images/stories/tux/podoxav-tortue-geniale-tux-1694.png\" width=\"256\" height=\"256\" style=\"float: right;\" hspace=\"6\" alt=\"Image\" title=\"Image\" border=\"0\" />Un po&#39; di barzellette e prese in giro su matematici, fisici, ingegneri, informatici.. (spero che nessuno si offenda!)...mi dispiace che i non addetti ai lavori non le troveranno molto divertenti... Me l&#39;ha mandato un&#39;amica di Trieste ma leggendo qua e l&agrave; mi sembra che sia stato preso tutto dal MIO sito!!!!Leggere per credere!<br /></em><br />HEISENBERG POTREBBE ESSERE STATO QUI <em>(Trovato in un bagno pubblico)</em></p><p><em>Commento: </em>&quot;Sono a Ingegneria da tanto  tempo che non so pi&ugrave; se la figa &egrave; orizzontale o verticale&quot;. <em>Commento  con altra grafia: </em>&quot;Dipende dal sistema di riferimento&quot;.<em> (trovata da Ferri nel bagno sotto al DEIS)</em> </p>','<p>Potresti essere un fisico se:<br /></p><ul><li> Qualcuno ti chiede l&#39;ora a tu rispondi in metri</li><li>Non guardi da nessuna parte perch&eacute; non vuoi rompere la sua funzione d&#39;onda</li><li>Assumi che un cavallo sia una sfera per averne funzioni matematiche pi&ugrave; semplici</li><li> Quando il tuo supervisore ti chiede dov&#39;&egrave; il tuo rapporto di laboratorio, tu dici di averne determinato il moto con tanta precisione che potrebbe essere in qualunque punto dell&#39;universo</li><li>Eviti di mescolare il tuo caff&eacute; perch&egrave; non vuoi accrescere l&#39;entropia dell&#39;universo</li><li>Ti viene la nausea quando qualcuno dice &quot;forza centrifuga&quot;<br /></li></ul><p> <br /> Cosa ottieni se incroci un elefante con una banana?  <span class=\"moz-txt-tag\">|</span>elefante<span class=\"moz-txt-tag\">|</span>*<span class=\"moz-txt-tag\">|</span>banana<span class=\"moz-txt-tag\">|</span>*sen q<br /> <br /> Cosa ottieni se incroci un elefante con un alpinista?  Non &egrave; definito: l&#39;alpinista &egrave; uno &quot;scalare&quot;<br /> <br /> Cosa &egrave; porpora e commuta?  Un grappolo abeliano<br /> <br /><strong> </strong>Teorema<strong>. </strong>Un gatto ha nove code.<br /> Dimostrazione. Nessun gatto ha otto code. Un gatto ha  una coda in pi&ugrave; di nessun gatto. Quindi un gatto ha  nove code.<br /> <br /> Cosa &egrave; non orientabile e vive nel mare?  Mobius Dick<br /> <br /> Cosa ci fa un matematico al ristorante che discute  animatamente con la sua insalata? Detta le condizioni al contorno<br /> <br /> &#39;Telecom Italia, informazione gratuita... il numero da  lei selezionato &egrave; immaginario, si prega di riagganciare, ruotare di 90&deg; l&#39;apparecchio e  riprovare&#39;<br /> <br /> Ges&ugrave; davanti a una folla di credenti si leva in alto e  comincia a recitare: &#39;y=ax2+bx+c...&#39; Uno della folla  esclama: &#39;Ecco, ci risiamo, la solita parabola...<br /> <br /> Siamo a una festa di fine anno dei logaritmi; tutti  ballano e si divertono in discoteca, pure sen(x) e  cos(x) (la tangente no perch&eacute; &egrave; un po&#39; montata...)  hanno accantonato per una volta le inimicizie e  ballano allegramente sui cubi, quand&#39;ecco che si nota,  in un cantuccio, un povero e^x che piange solo  soletto. Ecco che ln(x), il capo dei logaritmi  (log10(x) &egrave; stato cacciato perch&eacute; s&#39;incasinava con le  derivate), va da lui e gli dice: &#39;Su, vieni a ballare,<br /> unisciti a noi...&#39; e lui: &#39;No, no...&#39;; &#39;Ma su,  dai,integrati!&#39; e l&#39;altro: &#39;Tanto cosa cambia?&#39;<br /> <br /> - Babbo, babbo, mi faresti il compito di matematica  stasera? <br /> - No, figliolo, non sarebbe corretto<br />- Beh, puoi sempre provare. <br /> <br /> Per 3 punti non allineati passa una ed una sola retta,  purch&eacute; abbastanza spessa <br /><em> (Sentita dal professor Barozzi)</em><br /> La trigonometria serve soprattutto a 3 categorie di  persone:  ai cartografi,  ai naviganti (ed astronomi),  ma soprattutto agli scrittori di libri di  trigonometria.<br /><em> (Sentita dal professor Barozzi)</em><br /> <br /> Qualunque teorema tu abbia dimostrato esiste sempre un  giapponese piccolo ad arbitrio che l&#39;ha trovato prima  di te<br /><em> (Sentita dal professor Barozzi)</em><br /> <br /> Una sfera passeggia nel paese di Geometria.  Incontra un prisma che la saluta: &quot;Buongiorno, signor  cubo&quot;.  La sfera rimane un po&#39; perplessa, si guarda, e poi  prosegue la sua passeggiata.  Poco dopo incontra un parallelepipedo che la saluta:  &quot;Buongiorno signor cubo&quot;.  La sfera e&#39; ancora pi&ugrave;&#39; perplessa, ma continua la sua  passeggiata.  Incontra un rombo che la saluta ancora nello stesso  modo.&nbsp;  La sfera e&#39; oramai molto preoccupata e la sua psiche  sta vacillando quando incontra un icosaedro che le  dice: &quot;Buongiorno, signora sfera&quot;.  &quot;Oh, finalmente uno che non mi prende per il cubo !&quot;<br /> <br /> La top 19 di come preferiamo farlo..<br /> 1) Galois lo faceva la notte prima. <br /> 2) Moebius lo faceva sempre dalla stessa parte.<br /> 3) Gli algebristi lo fanno in gruppo.<br /> 4) I combinatori lo fanno in tutti i modi possibili.<br /> 5) I matematici non lo fanno: lo lasciano come  esercizio al lettore.<br /> 6) I fisici matematici capiscono la teoria di come si  fa, ma hanno difficolt&agrave; per ottenere risultati pratici<br /> 7) Markov lo fa incatenato.<br /> 8) I veri analisti lo fanno quasi ovunque.<br /> 9) Gli statistici probabilmente non lo fanno.<br /> 10) I fisici quantistici possono sapere quanto veloce  vanno, o dove lo fanno, ma non entrambe le cose. <br /> 11) [I logici lo fanno] o [non (i logici lo fanno)].<br /> 12) Gli informatici lo fanno a partire dal pi&ugrave; basso  (depth-first, poco traducibile...)<br /> 13) Fermat cerc&ograve; di farlo nel margine, ma non ci stava  dentro.<br /> 14) Gli aerodinamici lo fanno raccolti.<br /> 15) I cosmologi lo fanno nei primi 3 minuti.<br /> 16) I teorici dei gruppi lo fanno con Il Mostro. <br /> 17) I matematici puri lo fanno con rigore.<br /> 18) I programmatori di C lo fanno con i puntatori  <em>long</em>.<br /> 19) I topologi lo fanno apertamente.<br /> <br /> Un promettente laureando in fisica sta presentando la  sua tesi di laurea.<br /> Procede con calcoli e derivate varie, finch&eacute; non  ottiene qualcosa tipo: <br /> F = - m a<br /> Questi &egrave; visibilmente imbarazzato, e cos&igrave; il suo  supervisore, e idem il&nbsp;  resto della commissione.<br /> Lo studente tossisce nervosamente e dice: &quot;Sembra che  io abbia fatto un piccolo errore da qualche parte.&quot;<br /> Uno dei matematici della commissione replica asciutto: &quot;O questo o un numero dispari di essi!&quot;<br /> <br /> Due matematici sono al bar.&nbsp;  Il primo dice al secondo che la persona media sa ben  poco di matematica di base.&nbsp;  Il secondo &egrave; in disaccordo, e dice che la maggior  parte della gente pu&ograve; cavarsela con una quantit&agrave;  ragionevole di problemi.  Il primo se ne va al bagno, e in sua assenza il  secondo chiama la cameriera.  Le dice che tra pochi minuti, quando sar&agrave; tornato il  suo amico, la richiamer&agrave; e le porr&agrave; una domanda.&nbsp;  Tutto quello che lei dovr&agrave; fare sar&agrave; rispondere &quot;<em>un  terzo X cubo</em>&quot;.&nbsp; Lei ripete: &#39;un ter ... il cubo?&#39;&nbsp;  Lui ripete: &#39;un terzo x cubo&#39;.  Lei: &#39;un ter zicscubo&#39; <br /> `S&igrave;, va bene, dice lui.  Cos&igrave; lei annuisce, e se ne va mugugnando tra s&eacute;, &#39;un  ter zicscubo, un ter zicscubo...&#39;. <br /> Il primo torna e il secondo propone una scommessa per  provare la sua tesi, che la maggior parte della gente<br /> sa qualcosa di matematica.  Dice che chieder&agrave; alla bionda cameriera un integrale,  e il primo ridendo&nbsp;  annuisce.<br /> Il secondo uomo la chiama e le chiede &#39;qual &egrave;  l&#39;integrale di x quadro?&#39;.<br /> La cameriera dice &#39;un terzo x cubo&#39; e, mentre se ne  va, si volta e dice &#39;pi&ugrave; una costante!!`<br /> <br /> Un ingegnere, un fisico e un matematico sono rinchiusi  ognuno in una stanza con una scorta di scatolette di<br /> cibo, ma senza apriscatole.&nbsp;  Dopo un mese le porte delle tre stanze vengono aperte.  L&#39;ingegnere e&#39; bello grasso e tutte le scatolette sono  state aperte.&nbsp;  Alla richiesta di come abbia fatto, risponde che ha  usato la fibbia della cintura per farsi un  apriscatole.&nbsp;  Anche il fisico se l&#39;&egrave; cavata: circa la meta&#39; delle  scatolette e&#39; stata aperta.&nbsp;  La sua risposta alle domande dei ricercatori e&#39; un  lungo panegirico a base di &quot;Applicando il vettore  forza alla...&quot;, &quot;considerando&nbsp; l&#39;energia potenziale del  contenuto della scatoletta...&quot;.&nbsp;  Comunque in realt&agrave; le scatolette sono state aperte a<br /> furia di botte sul muro.&nbsp;  Infine e&#39; aperta la porta della stanza del matematico:  questi e&#39; paurosamente magro e denutrito; tutte le  scatolette sono chiuse.&nbsp;  Il poveraccio ne tiene una nella mano rinsecchita e la  regge a pochi centimetri dal viso, la fissa con occhi  spiritati e mormora: &quot;Supponendo, per assurdo, che sia  aperta...&quot;.<br /> <br /> Due uomini su una mongolfiera si perdono nel deserto  del Sahara.&nbsp;  Vedono un vecchio all&#39;ombra di una palma e scendono a  chiedere informazioni: &quot;Dove siamo?&quot;.&nbsp;  Dopo lunghi secondi di riflessione il vecchio  risponde: &quot;Su un pallone&quot;.  &quot;Grazie, signor matematico&quot;.  Il vecchio, meravigliato, chiede: &quot;Ma come avete  saputo che io sono un matematico?&quot;.  &quot;Per tre ragioni: la prima perch&eacute;&#39; avete riflettuto a  lungo prima di rispondere; la seconda perch&eacute;&#39; la<br /> vostra risposta e&#39; stata molto precisa; la terza perch&eacute;&#39; non serve a niente&quot;.<br /> <br /> Recenti studi sulla sessualit&agrave;&#39; femminile hanno  determinato quattro tipi di donne.&nbsp;  Esistono: la donna asmatica, quella religiosa, quella matematica e quella assassina. &nbsp; L&#39;asmatica fa (inspirando): &quot;Ahhhh ahhhhhhh ahh  ahhhhhhhhhhh&quot;. <br /> La religiosa: &quot;Dio... Dio... Dioooo mio....&quot;.&nbsp;  La matematica: &quot;Di pi&ugrave;&#39;... di pi&ugrave;&#39; di pi&ugrave;&#39; di pi&ugrave;&#39;!!!&quot;.&nbsp;  L&#39;assassina: &quot;Se lo tiri via ti ammazzo!&quot;. <br /> <br /> Sapete perche&#39; i matematici non fanno l&#39;amore? Perch&eacute;&#39; aggiungendo e togliendo una stessa quantita&#39;  non cambia nulla!!!<br /> <br /> Colmo per un matematico: vivere in una frazione. <br /> <br /> Perch&eacute;&#39; le donne sono brave in matematica?&nbsp; Perch&eacute;&#39; in un&#39;equazione prendono un membro, lo mettono  tra parentesi, lo elevano alla massima potenza e  infine lo riducono ai minimi termini!<br /> <br /> Ad una presentazione, Wirth (l&#39;inventore del Pascal)  disse:&quot;Potete chiamarmi per nome, nel qual caso mi  chiamerete Wirth, o per valore, nel qual caso mi  chiamerete maestro&quot;.<br /> <br /> Quattro ingegneri si trovano in macchina a dibattere  allegramente quando improvvisamente la macchina si  ferma.<br /> L&#39;elettronico dice: certamente sar&agrave; un problema di  centralina, ci do un&#39;occhiata...  Il chimico: mann&ograve;, idioti! E&#39; il rapporto tra ottani  ed ettani... si sente dal rumore!&nbsp;  Il meccanico: a me pare invece sia un problema di  pistoni, il motore fa un po&#39; fatica..  Salta su l&#39;informatico: sentite, ho un&#39;idea: usciamo e  rientriamo, magari  dopo riparte! <br /> <br /> Una squadra di ingegneri deve misurare l&#39;altezza  dell&#39;asta di una bandiera. Questi hanno solo del  nastro per misurarla, e divengono via via pi&ugrave;  frustrati perch&eacute; questo cade sempre. Passa un  matematico, sente il problema, e procede rimuovendo &nbsp; l&#39;asta dal terreno e misurandola agevolmente. Quando  se ne va, un ingegnere dice all&#39;altro: &#39;Tipico da  matematico! Abbiamo bisogno di sapere l&#39;altezza, e sto  qua ci d&agrave; la lunghezza!&#39;<br /> <br /> L&#39;ingegnere non vive... FUNZIONA. <em>(trovato in un&#39;aula di Ingegneria a Bologna) </em><br /> <br /> Il 93,7% delle statistiche &egrave; fasullo. <br /> <br /> Ingegneri, fisici e matematici a confronto.  Ad un matematico, un fisico e a un ingegnere &egrave; dato  uno stesso problema: Provare che tutti i numeri  dispari maggiori di due sono primi.&nbsp;  Procedono cos&igrave;:<br /> Matematico: 3 &egrave; primo, 5 &egrave; primo, 7 &egrave; primo, 9 non &egrave;  primo - controesempio, la tesi &egrave; falsa.<br /> Fisico: 3 &egrave; primo, 5 &egrave; primo, 7 &egrave; primo, 9 &egrave; un errore  sperimentale, 11 &egrave; primo, ... <br /> Ingegnere: 3 &egrave; primo, 5 &egrave; primo, 7 &egrave; primo, 9 &egrave; primo ,  11 &egrave; primo, ...<br /> Informatico: 1 &egrave; primo, 1 &egrave; primo, 1 &egrave; primo, 1 &egrave;  primo, .. <br /> Informatico sotto Unix: 3 &egrave; primo, 5 &egrave; primo, 7 &egrave;  primo, <em>segmentation fault</em>.&nbsp; S&igrave;, sono tutti primi.<br /> <br /> Nel considerare il comportamento di un cannone:<br /> * Un matematico sar&agrave; in grado di calcolare dove la  palla atterrer&agrave;<br /> * Un fisico sar&agrave; in grado di spiegare come la palla ci  arriva<br /> * Un ingegnere sar&agrave; l&agrave; a tentare di prenderla.<br /> <br /> Ad un gruppo delle menti pi&ugrave; dotate al mondo viene  posto uno stesso problema:&quot;Quant&#39;&egrave; 2+2?&quot;&nbsp;  L&#39;ingegnere tira fuori il suo manabile, lo sfoglia  avanti e indietro, e infine annuncia &quot;3,99&quot;.  Il fisico consulta i suoi riferimenti tecnici,imposta<br /> il problema al computer, e annuncia &quot;sta tra 3,98 e  4,02&quot;.  Il matematico medita per un po&#39;, in trance per il<br /> resto del mondo, quindi annuncia: &quot;Non so quale sia la  risposta, ma posso provare che una risposta  esiste!&quot;.<br /> Filosofo: &quot;Ma cosa intendi per 2+2?&quot;  Commercialista: Chiude tutte le porte e le finestre,  si guarda attentamente intorno poi chiede: &quot;Quale vuoi  che sia la risposta?&quot;  Hacker: entra nel super-computer della NASA e d&agrave; la  risposta.<br /> <br /> Cos&#39;&egrave; <em>pi greco</em>?<br /></p><ul><li>Matematico: Pigreco &egrave; il numero che esprime il  rapporto tra la circonferenza di un cerchio e il suo diametro. </li><li>  Fisico: Pigreco &egrave; 3,1415927 pi&ugrave; o meno 0.000000005 </li><li>Ingegnere: Pigreco &egrave; circa 3.</li></ul><p> <br /> Uno statistico, un matematico, un ingegnere e un  fisico sono fuori a caccia  insieme. Scorgono un cervo<br /> nella foresta.&nbsp;  Il fisico calcola la velocit&agrave; del cervo e l&#39;effetto  della gravit&agrave; sul proiettile, prende la mira col<br /> fucile e spara.  Ahim&eacute;, sbaglia; il proiettile passa 1 metro dietro al &nbsp; cervo.&nbsp;  Il cervo balza di qualche metro pi&ugrave; in l&agrave;, ma si  ferma, ancora visibile ai quattro.&nbsp;  &quot;Colpa tua se hai sbagliato, &quot;commenta l&#39;ingegnere,  &quot;ma ovviamente con una pistola ordinaria, uno se lo  sarebbe aspettato.&quot; Ecco che tira fuori la sua  speciale pistola caccia-cervi, che ha messo insieme  con un fucile ordinario, un astrolabio, un compasso,  un barometro, e un mucchio di luci accecanti che non<br /> fanno nulla se non impressionare gli osservatori, e  spara. Ahim&egrave;, il suo proiettile passa 1 metro davanti  al cervo, che questa volta rinsavisce e scappa al  sicuro.  &quot;Beh,&quot; dice il fisico, &quot;neanche il tuo colpo l&#39;ha  beccato.&quot;&nbsp;  &quot;Ma cosa dici?&quot; salta su lo statistico.&nbsp;  &quot;Tra voi due, &egrave; stato un colpo perfetto!&quot;&nbsp;  Come sapevano che era un cervo:  Il fisico osserva che si comportava come un cervo,  dunque dev&#39;essere un cervo.  Il matematico chiede al fisico cos&#39;era, riducendosi<br /> dunque al caso precedente.  L&#39;ingegnere era nella foresta a caccia di cervi,  quindi era un cervo. <br /> <br /> Un matematico, un fisico, e un ingegnere stanno  viaggiando per la Scozia quando vedono una pecora nera  attraverso la finestra del treno.  &quot;Ha ha,&quot; dice l&#39;ingegnere, &quot;vedo che le pecore scozzesi  sono nere.&quot;&nbsp;  &quot;Mm,&quot; dice il fisico, &quot;volevi dire che alcune pecore  scozzesi sono nere.&quot;&nbsp;  &quot;No,&quot; dice il matematico, &quot;Tutto ci&ograve; che sappiamo &egrave;  che c&#39;&eacute; almeno una pecora in Scozia, e che almeno un  lato di quella pecora &egrave; nero!&quot;<br /> <br /> Un Matematico (M) e un Ingegnere (I) frequentano una  lezione di un Fisico. L&#39;argomento concerne le teorie<br /> di Kulza-Klein riguardo i processi fisici che  avvengono negli spazi di dimensione 9, 12 e anche  maggiori.&nbsp;  Il M &egrave; seduto, chiaramente gradisce la lezione, mentre  l&#39;I &egrave; corrucciato e sembra generalmente confuso e in  difficolt&agrave;.  Alla fine l&#39;I ha un terribile mal di testa.  Alla fine, il M fa commenti sull&#39;ottima lezione.<br />I: &quot;Come fai a capire sta roba?&quot; <br />M:&quot;Semplicemente visualizzo il procedimento&quot; <br />I:&quot;Come puoi davvero visualizzare qualcosa che avviene  in uno spazio 9-dimensionale?&quot;&nbsp;  <br />M: &quot;Facile: prima lo visualizzi in uno spazio  n-dimensionale, poi poni n=9&quot;<br /> <br /> Cosa hanno in comune un matematico e un ingegnere?  Sono entrambi stupidi, con l&#39;eccezione del matematico<br /> <br /> Un dottore, un avvocato e un matematico discutono sui  meriti relativi dell&#39;avere una moglie o una maestra. L&#39;avvocato dice: &quot;Certamente una maestra &egrave; meglio. Se  hai una moglie e vuoi un divorzio, procura un sacco di<br /> problemi legali.&quot; &nbsp; Il dottore dice: &quot;E&#39; meglio avere una moglie perch&eacute; il  senso di sicurezza abbassa il tuo stress e fa bene  alla salute.&nbsp;  Il matematico dice: &quot;Avete torto entrambi, E&#39; meglio  avere entrambe cos&igrave; che quando tua moglie crede che tu  sia con la maestra e la maestra crede tu sia con tua  moglie... puoi fare un po&#39; di matematica!!&quot;<br /> <br /> Per capire la differenza tra un matematico e un  ingegnere fai il seguente  esperimento. Metti una  pentola piena d&#39;acqua in mezzo al pavimento della  cucina e di&#39; al tuo soggetto di bollire l&#39;acqua.  L&#39;ingegnere metter&agrave; la pentola sul fornello e  accender&agrave; il fuoco.  Il matematico far&agrave; la stessa cosa.&nbsp;  Poi, metti la pentola sul fornello, e chiedi al<br /> soggetto di bollire l&#39;acqua.  L&#39;ingegnere accender&agrave; la fiamma.&nbsp;  Il matematico metter&agrave; la pentola in mezzo al pavimento<br /> della cucina... riconducendosi dunque ad un problema  gi&agrave; risolto!&nbsp; <br /> <br /> Un ingegnere, un fisico e un matematico stanno in un  hotel mentre frequentano un seminario tecnico.&nbsp;  L&#39;ingegnere si sveglia sentendo odore di fumo. Esce  nel corridoio e vede un fuoco, allora riempie il  bidone d&#39;acqua,e ferma il fuoco. Se ne torna a letto.&nbsp;  Dopo, il fisico si sveglia e sente odore di fumo. Apre  la porta e vede un fuoco nel corridoio. Scende gi&ugrave;  nella hall fino a un estintore e dopo aver calcolato  velocit&agrave; del fuoco, distanza, pressione dell&#39;acqua,<br /> traiettoria, etc. estingue il fuoco con la minima  quantit&agrave; d&#39;acqua ed energia richieste. Dopo, il  matematico si sveglia e sente odore di fumo. Va nella  hall, vede il fuoco e poi l&#39;estintore. Pensa per un  momento e quindi esclama, &quot;Ah, una soluzione esiste!&quot;  e quindi de ne torna a dormire.<br /> <br /> Un matematico, un ingegnere ed un fisico hanno un  colloquio per un&#39;assunzione. In ciascun caso, il  colloquio procede bene fino alla penultima domanda;  poi viene chiesto: &quot;Quanto fa uno pi&ugrave; uno?&quot;. Ognuno  dei tre sospetta una domanda trabocchetto, ed esita a  rispondere.&nbsp;  Il matematico pensa per un momento, poi dice: &quot;Non ne  sono certo, ma credo che converga&quot;.&nbsp;  Il fisico dice: &quot;Non sono sicuro, ma credo che sia  dell&#39;ordine di uno&quot;.  L&#39;ingegnere si alza, chiude la porta dell&#39;ufficio e  dice: &quot;Quanto vuole che faccia?&quot;. <br /> <br /> In un sistema cartesiano, ogni punto del piano era schedato. <br /> <br /> Persa la ragione, la progressione smise di progredire.<br /> <br /><img src=\"http://www.palladius.it/joomlait//images/stories/ric-lavoro1.jpg\" width=\"230\" height=\"270\" style=\"float: right;\" hspace=\"6\" alt=\"Image\" title=\"Image\" border=\"0\" /> &nbsp; Ricomponiti, disse la parentesi al polinomio; cosi&#39;  scomposto, ti si vedono i binomi. <br /> <br /> Immersa da tempo nella tavola pitagorica, l&#39;equazione  aveva sviluppato radici numerose e complesse. <br /> <br /> Quella funzione ha un seno iperbolico, disse il  logaritmo leccandosi la mantissa. <br /> <br /> Il polinomio era ormai in avanzata decomposizione,  tanto che i monomi e i binomi si staccavano a pezzi. <br /> <br /> In fondo ho dei numeri, penso&#39; la parentesi: potrei  diventare una potenza. <br /> <br /> L&#39;equazione stava cercando di risalire alle radici per  trovare la propria identit&agrave;. <br /> <br /> Convinto dalla tangente, il cerchio accett&ograve; di  trasformarsi in quadrato.  L&#39;angolo invece rifiuto&#39;: era sempre stato retto e  tale voleva restare.<br /> <br /> Quando andava al circolo, il diametro passava sempre  per il centro. <br /> <br /> Purtroppo aveva una radice cubica, e l&#39;estrazione  sarebbe stata difficile.<br /> <br /> Priva di indice, la radice quadrata non aveva fatto&nbsp; il  servizio militare. <br /> <br /> La radice di due era molto preoccupata: ormai erano  passati trenta decimali senza che le venisse il  periodo. Temeva di essere incinta, anche se ci&ograve; le  sembrava irrazionale. <br /> <br /> Ma tutto cio&#39; e&#39; immaginario, disse il radicale puntando l&#39;indice accusatore su meno uno. <br /> <br /> Facciamo quadrato, grido&#39; l&#39;ipotenusa ai cateti:  arrivano i rettangoli!</p>','1','3','0','1','2006-01-26 13:05:29','62','Riccardo Carlesso','2007-12-26 11:13:57','64','0','0000-00-00 00:00:00','2006-01-26 01:00:00','0000-00-00 00:00:00','tux/podoxav-tortue-geniale-tux-1694.png|right||0||bottom||\r\nric-lavoro1.jpg|right||0||bottom||','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','6','0','7','','','0','828','matematici-e-ingegneri');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '35','Pesata diabolica','','<p>Ci sono 12 sfere metalliche tutte identiche come aspetto.</p><p>Sappiamo che una ha un peso differente dalle altre 11, &nbsp;ma&nbsp;non sappiamo se sia&nbsp;pi&ugrave; leggera o pi&ugrave; pesante.</p><p>&nbsp;Vogliamo identificarla, e&nbsp;abbiamo a disposizione solo&nbsp;<strong>3</strong> pesate su una bilancia a 2 piatti.</p>','','1','3','0','14','2006-01-27 13:00:27','63','','2006-02-15 11:03:42','0','0','0000-00-00 00:00:00','2006-01-27 00:00:00','0000-00-00 00:00:00','','','','1','0','5','','','1','272','pesata-diabolica');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '36','Foto!!','Foto!!','<p>Ho buttato su un nuovo modulino con le <strong>foto</strong>|||<br />Perch&egrave; non provate a giocarci buttando su quel che volete?!? Vi prego: cagate, foto vostre, foto di amici, da internet: devo vedere coem funziona l\'upload in comunit&agrave;... grazie!<br /></p><p><strong>PS</strong>. Stamane in piscina mi son tagliato un dito. Pare vada tutto bene.&nbsp;</p>','','-1','2','0','3','2006-01-29 21:16:50','62','','2006-09-14 11:57:46','64','0','0000-00-00 00:00:00','2006-01-29 01:00:00','0000-00-00 00:00:00','','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','3','','','0','526','foto');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '37','Un po\' di calcolo combinatorio (abbastanza difficile)','','<p>Finalmente riesco a scrivere qualcosa<img border=\"0\" alt=\"Tongue out\" src=\"mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-tongue-out.gif\" />...spero di riuscire a proporvi indovinelli e quesiti stuzzicanti...cominciamo con un po\' di calcolo combinatorio.</p><p>&nbsp;In quanti modi si possono disporre tutti i numeri naturali da 1 a 9 nelle nove caselle di una griglia 3x3 in modo che i numeri presenti in due caselle contigue (in orizzontale e in verticale) non diano mai prodotto e somma entrambi pari ?</p><p>Buon lavoro e...non provate a contarli uno per uno !!!<img border=\"0\" alt=\"Wink\" src=\"mambots/editors/tinymce/jscripts/tiny_mce/plugins/emotions/images/smiley-wink.gif\" /> <br /> </p>','','1','3','0','14','2006-02-01 13:49:05','70','','2006-02-15 11:03:42','0','0','0000-00-00 00:00:00','2006-02-01 00:00:00','0000-00-00 00:00:00','','','','1','0','4','','','0','493','un-po-di-calcolo-combinatorio-abbastanza-difficile');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '38','MEDIA UPLOAD','','<table border=\"0\"><tbody><tr><td>  <p><strong>Aiuto</strong>!</p> <p>Ho installato un pacchetto<em> FINE DI MONDO</em> che uploada <strong>foto</strong>, <strong>mp3 </strong>eccetera. Puoi uploadare con JAVA facendo drag n drop, oppure buttando su un file o 5 file alla volta, o addirittura puoi buttar su un unico zippone che viene poi aperto e scompattato.</p> <p><u>MI FATE DAVVERO FELICE</u> se ci armeggiate buttando su tutto ci&ograve; che volete (purch&egrave; legale!) e poi magari mi mandate feedback...</p> <p>Grazie, Ric.&nbsp;</p> </td><td><img src=\"http://www.palladius.it/joomlait/images/stories/ric-lavoro1.jpg\" border=\"0\" alt=\" \" height=\"80\" /> <br /> </td></tr></tbody></table> ','','1','2','0','3','2006-02-01 21:22:00','72','','2006-09-14 11:54:53','64','0','0000-00-00 00:00:00','2006-02-01 01:00:00','0000-00-00 00:00:00','ric-lavoro1.jpg|||0||bottom||','','pageclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\npdf=\nprint=\nemail=\nkeyref=\ndocbook_type=','2','0','2','','','0','112','media-upload');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '39','Terne carlessiane','','<p>Bella regaz!!</p><p>Oggi ho inventato una nuova terna pitagorica.<br /> l\'insieme dei numeri a,b,c tali che<br /><strong> a<sup>2</sup>+b<sup>2</sup>=c<sup>2</sup></strong><br /></p><p> dove per&ograve; il &quot;+&quot; &egrave; la concatenazione tra stringhe.<br /> ne ho trovato finalmente uno non banale....<br />Mi spiego: esiste un quadrato perfetto tale che se lo scindete (tipo: 144 -&gt; 1 e 44... NO, 14 e 4... NO).</p>','Anzi, facciamo cos&igrave;: io vi dico quale ho trovato io, voi me ne trovate altri, ok?!?<p><strong>1681=16|81</strong><br /> <br /> vabb&egrave;, vabb&egrave;, Pitagora &egrave; + utile, vabb&egrave;....</p>','1','1','0','15','2006-02-02 16:27:49','64','','2006-02-15 11:03:42','64','0','0000-00-00 00:00:00','2006-02-02 00:00:00','0000-00-00 00:00:00','','','','2','0','7','','','0','449','terne-carlessiane');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '40','quote','','<div align=\"left\"><strong><font size=\"1\" face=\"Courier New\"><span style=\"font-size: 9pt\">Nessun uomo intelligente contraddice sua moglie.</span></font></strong></div><div align=\"left\"><strong><font size=\"1\" face=\"Courier New\"><span style=\"font-size: 9pt\">Aspetta che lo faccia da sola.</span></font></strong></div><blockquote><div align=\"left\"><em><font size=\"1\" face=\"Courier New\"><span style=\"font-size: 9pt\">(Humphrey Bogart)</span></font></em></div></blockquote>','','0','3','0','18','2006-02-06 12:57:42','64','','2006-07-17 10:23:43','64','0','0000-00-00 00:00:00','2006-02-06 00:00:00','0000-00-00 00:00:00','','','','5','0','31','','','0','204','quote');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '41','quote2','','<pre><strong>feature</strong>, n:         <br />         A surprising property of a program.  Occasionaly documented.  To<br />         call a property a feature sometimes means the author did not<br />         consider that case, and the program makes an unexpected, though<br />         not necessarily wrong response.  See BUG.  &quot;That\'s not a bug, it\'s<br />         a feature!&quot;  A bug can be changed to a feature by documenting it. </pre>','','0','3','0','18','2006-02-06 19:06:06','64','','2006-06-29 09:46:07','64','0','0000-00-00 00:00:00','2006-02-06 00:00:00','0000-00-00 00:00:00','','','','3','0','29','','','0','208','quote2');
INSERT INTO jos_content (`id`,`title`,`title_alias`,`introtext`,`fulltext`,`state`,`sectionid`,`mask`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`parentid`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`alias`) VALUES ( '42','Chuck Norris (esteso!)','','Chuck Norris &egrave; stato capace di vincere una partita a scacchi anche dopo che gli avevano mangiato il re. <br /> <br />Chuck Norris una volta ha trovato lavoro in un lunapark. Era il calcinculo. <br /> <br />Chuck Norris un giorno ha creato un lanciafiamme urinando in un accendino. <br /> <br />Una notte, durante un temporale, Chuck Norris &egrave; stato colpito da un fulmine. Il fulmine &egrave; morto sul colpo. <br /> <br />Chuck Norris ha creato un milione di posti di lavoro. Tutti becchini. <br /> <br />Chuck Norris da piccolo amava i cartoni. Soprattutto allo stomaco. ','Chuck Norris guarda la videocassetta di &quot;The ring&quot; tutte le sere e il suo telefono non squilla mai. <div class=\"moz-text-flowed\" style=\"font-family: -moz-fixed; font-size: 13px\"><p>Il governo degli Stati Uniti spende 40 miliardi di dollari all&#39;anno in prevenzione di calamit&agrave; naturali. Di questi, 35 miliardi sono spesi per garantire che la birra di Chuck Norris sia sempre fredda. </p><p>Un giorno, dopo aver sentito &quot;I will survive&quot;, Chuck Norris decapit&ograve; Gloria Gaynor con un calcio rotante: l&#39;aveva interpretata come una sfida. </p><p>Chuck Norris ha fatto il suo primo milione di dollari all&#39;et&agrave; di sei anni grazie alla fatina dei dentini. I denti che lasciava sotto il cuscino per&ograve; non erano i suoi. </p><p>I comunisti in realt&agrave; non mangiano i bambini, perch&egrave; se li mangia prima Chuck Norris. I comunisti. E anche i bambini. </p><p>Durante un concerto dei Pooh, Chuck Norris ha fermato la musica. </p><p>Chuck Norris ha contato fino ad infinito. Due volte </p><p>Quando Chuck Norris fissa il sole, &egrave; il sole a distogliere lo sguardo per primo. </p><p>Sting riesce a fare sesso per dodici ore di fila. Chuck Norris riesce a non fare sesso per dodici ore di fila. </p><p>Chuck Norris mangia la pizza con le mani. A volte ci mette anche le olive. </p><p>Il segnale con l&#39;uomo in carrozzella non indica che un parcheggio sia riservato ai disabili, ma che appartiene di diritto a Chuck Norris, e che verrai ridotto su una sedia a rotelle se oserai parcheggiarci sopra. </p><p>Chuck Norris salta i fossi per lungo. </p><p>Chuck Norris fuma toscani. Vivi. </p><p>Chuck Norris pu&ograve; superare il tonno insuperabile. A destra. </p><p>Per diventare famoso, il diavolo ha venduto l&#39;anima a Chuck Norris. </p><p>Chuck Norris taglia i grissini col tonno. </p><p>In una rara scena tagliata de &quot;L&#39;Impero colpisce ancora&quot;, Chuck Norris rivela a Darth Vader di essere suo padre. </p><p>Chuck Norris pu&ograve; sbattere una porta girevole. </p><p>Chuck Norris pu&ograve; essere visto ad occhio nudo dallo spazio. </p><p>Il primo a dividere l&#39;atomo non &egrave; stato Einstein. E&#39; stato Chuck Norris: l&#39;ha frantumato con un solo calcio rotante. </p><p>Berlusconi pu&ograve; unire la Sicilia all&#39;Italia con un ponte. Chuck Norris pu&ograve; farlo a mani nude. </p><p>A morra cinese, Chuck Norris sconfigge carta, forbice e sasso contemporaneamente. </p><p>Ci sono solo quattro cavalieri dell&#39;Apocalisse, perch&egrave; Chuck Norris preferisce andare a piedi. </p><p>McGyver pu&ograve; costruire un aereo con una gomma da masticare e delle graffette, ma Chuck Norris pu&ograve; spiaccicargli la testa contro un muro con un calcio rotante e portarglielo via. </p><p>Se Bruce Banner perde il controllo si trasforma nell&#39;incredibile Hulk. Se l&#39;incredibile Hulk perde il controllo si trasforma in Chuck Norris. </p><p>Se Chuck Norris si sveglia con la luna storta, la raddrizza a calci rotanti. </p><p>Quando Chuck Norris ha lasciato le sue impronte delle mani e dei piedi sulla Hollywood Walk of Fame, il cemento era asciutto. </p><p>Nel Vangelo secondo Chuck Norris, Dio ha un ruolo di secondo piano. E di Ges&ugrave; non c&#39;&egrave; traccia. </p><p>L&#39;erba del vicino di Chuck Norris &egrave; meno verde della sua. </p><p>Chuck Norris detiene il record di punti in un&#39;unica partita di basket: centosettanta. Distribuiti equamente tra gambe, braccia e volto degli avversari. </p><p>Se mia nonna avesse le ruote, sarebbe una cariola. Se Chuck Norris avesse le ruote, sarebbe la batmobile. </p><p>Nella botola di Lost c&#39;&egrave; Chuck Norris. </p><p>Freddy Krueger non dorme mai per paura di sognare Chuck Norris. </p><p>Chuck Norris ha bandito gli arcobaleni dallo stato del North Dakota. </p><p>Chuck Norris non segue la moda: la moda segue Chuck Norris. Poi lui si volta e la colpisce con un calcio rotante. </p><p>Se un battito d&#39;ala di farfalla in Brasile pu&ograve; scatenare un tornado in Oklahoma, un calcio rotante di Chuck Norris in Oklahoma spiega facilmente il Big Bang. </p><p>Godot non arriva perch&egrave; si &egrave; preso un calcio volante di Chuck Norris. </p><p>Quando Chuck Norris fa l&#39;occhiolino, le ragazze si sciolgono. Letteralmente. </p><p>Anche Chuck Norris ha un cuore. E lo usa come portachiavi. </p><p>Mio cuggino conosce un colpo segreto che se te lo d&agrave; dopo tre giorni muori. Gliel&#39;ha insegnato Chuck Norris. </p><p>Chuck Norris &egrave; particolarmente consigliato nelle diete povere di calcio. </p><p>Il presidente Roosevelt proclam&ograve;: &quot;L&#39;unica cosa di cui dobbiamo avere paura &egrave; la paura stessa. E Chuck Norris&quot;. </p><p>Alle morbide Fruit Joy, Chuck Norris pu&ograve; resistere. </p><p>Chuck Norris d&agrave; fuoco alle formiche con una lente di ingrandimento. Di notte. </p><p>Chuck Norris ha perso la verginit&agrave; prima di suo padre. </p><p>Chuck Norris ogni weekend uccide 14 bianchi, solo per dimostrare che non &egrave; razzista. </p><p>Contrariamente a quanto si crede, il vero nome di Chuck Norris &egrave; Chuck Morris. Purtroppo, una stanghetta della M &egrave; stata inavvertitamente amputata da un calcio rotante eseguito da Chuck all&#39;anagrafe. </p><p>I globuli bianchi di Chuck Norris sono sue copie in miniatura e sconfiggono i virus a calci rotanti. </p><p>Chuck Norris non bagnava il letto da bambino. Il letto si bagnava da solo per la paura. </p><p>Ne uccide pi&ugrave; la penna della spada. Chuck Norris uccide pi&ugrave; della penna. </p><p>Quando Chuck Norris fa le flessioni, non alza s&egrave; stesso, abbassa la Terra. </p><p>Uno studio ha rivelato che le principali cause di morte negli Stati Uniti sono:<br /></p><ol><li>infarto</li><li>Chuck Norris</li><li>cancro </li></ol><p>Chuck Norris non porta l&#39;orologio. Decide lui che ora &egrave;. </p><p>Il titolo originale di &quot;Alien vs Predator&quot; era &quot;Alien e Predator vs Chuck Norris&quot;. Il film &egrave; stato cancellato subito dopo essere entrato in preproduzione. Nessuno avrebbe pagato nove dollari per vedere un film che durava 14 secondi.</p>                                                     </div><div><p>&nbsp;</p></div><div align=\"center\"><p><em>&nbsp;Nuove avventure di superChuck!!</em></p></div><div class=\"moz-text-flowed\" style=\"font-family: -moz-fixed; font-size: 13px\"><p>  Quando Chuck Norris fa sesso con un uomo, non &egrave; perch&eacute; &egrave; gay, ma perch&eacute; aveva finito le donne. </p><p>&nbsp;Chuck Norris non legge i libri. </p><p>Li fissa fino a quando non ottiene le informazioni che gli servono. </p><p>Se chiedete l&#39;ora a Chuck Norris lui vi risponder&agrave; &quot;Ancora due secondi.&quot; </p><p>Dopo aver chiesto &quot;Ancora due secondi cosa?&quot;, vi colpisce con un calcio volante a girare.</p><p>Chuck Norris usa i profilattici stimolanti al rovescio, cos&igrave; pu&ograve; provare piacere. </p><p>Dal 1940, anno di nascita di Chuck Norris, le morti causate da calci volanti a girare sono aumentate del 13.000%. </p><p>Non ci sono disabili. Solo persone che hanno incontrato Chuck Norris. </p><p>Non c&#39;&egrave; mento dietro alla barba di Chuck Norris. C&#39;&egrave; solo un altro pugno. </p><p>Una volta si credeva che Chuck Norris avesse perso uno scontro con un pirata, ma &egrave; un falso, creato da Chuck Norris stesso per attirare pi&ugrave; pirati su di lui. </p><p>Chuck Norris una volta si &egrave; schierato per calciare il tiro di trasformazione di una partita di football tra scuole superiori.Visto che il pallone si era forato, ha persuaso gli arbitri a fargli effettuare il calcio con un bambino di 3 mesi. Chuck ha spedito 60 metri oltre i limiti del campo, e poi ha proceduto a scoparsi ogni ragazza dello stadio. </p><p>Quando la moglie di Chuck Norris ha bruciato il tacchino il giorno del Ringraziamento, Chuck ha detto: &quot;Non preoccuparti tesoro&quot; e si &egrave; diretto nel cortile dietro casa. Dopo cinque minuti &egrave; rientrato con un tacchino vivo, lo ha interamente ingoiato, e quando l&#39;ha ritirato fuori pochi secondi dopo era interamente cotto in salsa di mirtilli. Alla richiesta di spiegazioni da parte della moglie, Chuck l&#39;ha colpita in volto con un calcio volante e ha dichiarato: &quot;Mai discutere con Chuck Norris.&quot; </p><p>Quelli che scorrono alla fine di una puntata di Walker Texas Ranger non sono i titoli di coda; in realt&agrave; &egrave; la lista delle persone che hanno ricevuto un calcio volante in faccia da Chuck Norris quel giorno. </p><p>Le lacrime di Chuck Norris curano il cancro. Il problema &egrave; che lui non ha mai pianto. </p><p>Chuck Norris una volta diede un calcio volante cos&igrave; veloce che il suo piede oltrepass&ograve; la velocit&agrave; della luce, torn&ograve; indietro nel tempo e uccise Amelia Earhart mentre si trovava in volo sopra l&#39;Oceano Pacifico. </p><p>Chuck Norris non ha l&#39;AIDS, ma lo trasmette comunque alle persone. </p><p>Piuttosto che venire al mondo come un bambino normale, Chuck Norris ha deciso invece di guadagnarsi l&#39;uscita dalla placenta della madre a forza di pugni. Poco dopo gli spunt&ograve; la barba. </p><p>Chuck Norris ha costruito una macchina del tempo e l&#39;ha usata per tornare indietro in tempo per fermare l&#39;assassinio di JFK. Come Oswald spar&ograve;, Chuck riusc&igrave; a deviare la traiettoria dei tre proiettili con il solo uso della barba. </p><p>La testa di JFK per&ograve; esplose comunque dallo stupore. </p><p>Chuck Norris ha venduto l&#39;anima al diavolo in cambio della ruvida bellezza e dell&#39;incomparabile abilit&agrave; nelle arti marziali. Subito dopo aver concluso la transazione, Chuck colp&igrave; in faccia il diavolo con un calcio volante e si riprese indietro l&#39;anima. Il diavolo, che notoriamente apprezza l&#39;ironia, ammise lo sbaglio e dichiar&ograve; che avrebbe dovuto schivare il colpo invece di abbassare la guardia. La coppia ora si ritrova per il poker ogni secondo Mercoled&igrave; del mese. </p><p>Un uomo una volta chiese a Chuck Norris se il suo vero nome fosse &quot;Charles&quot;.<br />Chuck Norris non rispose, si limit&ograve; a fissare l&#39;uomo fino a farlo esplodere. </p><p>Chuck Norris ha recentemente avuto l&#39;idea di vendere la sua urina come bibita in lattina.<br />Il nome della bevanda &egrave; Red Bull. </p><p>Chuck Norris pu&ograve; portare una donna all&#39;orgasmo semplicemente indicandola con un dito e dicendo &quot;booya&quot;. </p><p>&nbsp;Chuck Norris non dorme. Aspetta. </p><p>&nbsp;Chuck Norris una volta abbatt&egrave; un&#39;aereo tedesco puntandolo con un dito e urlando &quot;Bang!&quot; </p><p>&nbsp;Dopo attenta analisi, il Presidente Truman scelse di lanciare la bomba atomica su Hiroshima all&#39;alternativa di mandare Chuck Norris. Il motivo fu che venne ritenuta la soluzione pi&ugrave; &quot;umana&quot;. </p><p>Chuck Norris spesso chiede alle persone di tirargli il dito. Quando lo fanno, li colpisce con un calcio volante nell&#39;addome. Dopodich&egrave; scoreggia.&nbsp;</p><p>Chuck Norris &egrave; attualmente in causa con l&#39;NBC, reclamando il fatto che Law and Order sono i nomi depositati per le sue gambe.&nbsp;</p><p>Se riuscite a vedere Chuck Norris, lui pu&ograve; vedere voi. Se non riuscite a vedere Chuck Norris potreste essere a pochi secondi dalla vostra morte. </p><p>Chuck Norris si &egrave; preso la mia verginit&agrave;, e sicuramente prender&agrave; anche la vostra. Se state pensando &quot;Impossibile, l&#39;ho gi&agrave; persa&quot;, vi state sbagliando di grosso.&nbsp;</p><p>Chuck Norris usa un serpente a sonagli vivo come preservativo. </p><p>Gli Alieni esistono. Stanno semplicemente aspettando la morte di Chuck Norris, prima di attaccare. </p><p>L&#39;ex-ragazza di Chuck Norris una volta gli chiese quanta legna avrebbe potuto fare una marmotta se le marmotte avessero potuto fare legna. Lui url&ograve;: &quot;COME OSI FARE RIME IN PRESENZA DI CHUCK NORRIS!&quot; e le tagli&ograve; la gola. Tenend&ograve; la gola della ragazza ancora sanguinante tra le mani, mugg&igrave;: &quot;Nessuno prende per il culo Chuck!&quot;. Due anni e cinque mesi dopo, cap&igrave; l&#39;ironia dell&#39;affermazione e rise cos&igrave; forte che chiunque nel raggio di 160km divent&ograve; sordo. </p><p>&nbsp;Chuck Norris era previsto come personaggio nel video game &quot;Street Fighter II&quot;, ma fu rimosso dai Beta Testers perch&egrave; ogni bottone gli faceva comunque compiere un calcio a girare. Quando gli viene chiesto di questo bug, Chuck dice &quot;Non era un bug.&quot;&nbsp;</p><h4>Presi da un altro sito...<br /></h4><p> Quando Chuck Norris fa sesso con un uomo, non &egrave; perch&eacute; &egrave; gay, ma perch&eacute; aveva finito le donne.<br /> <br /> Chuck Norris non legge i libri. Li fissa fino a quando non ottiene le informazioni che gli servono.<br /> <br /> Se chiedete l&#39;ora a Chuck Norris lui vi risponder&agrave; &quot;Ancora due secondi.&quot; Dopo aver chi